40 #else // MSVC
41 # if _MSC_VER >= 1800
42 #  include <stdbool.h>
43 # else // stdbool.h missing prior to MSVC++ 12.0 (VS2013)
44 //#  define bool int      
45 //#  define true 1
46 //#  define false 0
47 # endif
48 #endif
49 */
