232     template <typename T>
233     class TOML_EMPTY_BASES utf8_reader final : public utf8_reader_interface
234     {
235       private:
236         static constexpr size_t block_capacity = 32;
237         utf8_byte_stream<T> stream_;
238         source_position next_pos_ = { 1, 1 };
239 
240         impl::utf8_decoder decoder_;
241         struct currently_decoding_t
242         {
243             char bytes[4];
244             size_t count;
245         } currently_decoding_;
246 
247         struct codepoints_t
248         {
249             TOML_OVERALIGNED utf8_codepoint buffer[block_capacity];
250             size_t current;
251             size_t count;
252         } codepoints_;
253 
254         source_path_ptr source_path_;
255 
256 #if !TOML_EXCEPTIONS
257         optional<parse_error> err_;
258 #endif
259 
260         bool read_next_block() noexcept(!TOML_COMPILER_HAS_EXCEPTIONS)
261         {
262             TOML_ASSERT(stream_);
263 
264             TOML_OVERALIGNED char raw_bytes[block_capacity];
265             size_t raw_bytes_read;
266 
267             // read the next raw (encoded) block in from the stream
268             if constexpr (noexcept(stream_(raw_bytes, block_capacity)) || !TOML_EXCEPTIONS)
269             {
270                 raw_bytes_read = stream_(raw_bytes, block_capacity);
271             }
272 #if TOML_EXCEPTIONS
273             else
274             {
275                 try
276                 {
277                     raw_bytes_read = stream_(raw_bytes, block_capacity);
278                 }
279                 catch (const std::exception& exc)
280                 {
281                     throw parse_error{ exc.what(), next_pos_, source_path_ };
282                 }
283                 catch (...)
284                 {
285                     throw parse_error{ "An unspecified error occurred", next_pos_, source_path_ };
286                 }
287             }
288 #endif // TOML_EXCEPTIONS
289 
290             // handle a zero-byte read
291             if TOML_UNLIKELY(!raw_bytes_read)
292             {
293                 if (stream_.eof())
294                 {
295                     // EOF only sets the error state if the decoder wants more input, otherwise
296                     // a zero-byte read might have just caused the underlying stream to realize it's exhaused and set
297                     // the EOF flag, and that's totally fine
298                     if (decoder_.needs_more_input())
299                         utf8_reader_error("Encountered EOF during incomplete utf-8 code point sequence",
300                                           next_pos_,
301                                           source_path_);
302                 }
303                 else
304                 {
305                     utf8_reader_error("Reading from the underlying stream failed - zero bytes read",
306                                       next_pos_,
307                                       source_path_);
308                 }
309                 return false;
310             }
311 
312             TOML_ASSERT_ASSUME(raw_bytes_read);
313             std::memset(&codepoints_, 0, sizeof(codepoints_));
314 
315             // helper for calculating decoded codepoint line+cols
316             const auto calc_positions = [&]() noexcept
317             {
318                 for (size_t i = 0; i < codepoints_.count; i++)
319                 {
320                     auto& cp    = codepoints_.buffer[i];
321                     cp.position = next_pos_;
322 
323                     if (cp == U'\n')
324                     {
325                         next_pos_.line++;
326                         next_pos_.column = source_index{ 1 };
327                     }
328                     else
329                         next_pos_.column++;
330                 }
331             };
332 
333             // decide whether we need to use the UTF-8 decoder or if we can treat this block as plain ASCII
334             const auto ascii_fast_path = !decoder_.needs_more_input() && impl::is_ascii(raw_bytes, raw_bytes_read);
335 
336             // ASCII fast-path
337             if (ascii_fast_path)
338             {
339                 decoder_.reset();
340                 currently_decoding_.count = {};
341 
342                 codepoints_.count = raw_bytes_read;
343                 for (size_t i = 0; i < codepoints_.count; i++)
344                 {
345                     auto& cp    = codepoints_.buffer[i];
346                     cp.value    = static_cast<char32_t>(raw_bytes[i]);
347                     cp.bytes[0] = raw_bytes[i];
348                     cp.count    = 1u;
349                 }
350             }
351 
352             // UTF-8 slow-path
353             else
354             {
355                 // helper for getting precise error location
356                 const auto error_pos = [&]() noexcept -> const source_position&
357                 { //
358                     return codepoints_.count ? codepoints_.buffer[codepoints_.count - 1u].position : next_pos_;
359                 };
360 
361                 for (size_t i = 0; i < raw_bytes_read; i++)
362                 {
363                     decoder_(static_cast<uint8_t>(raw_bytes[i]));
364                     if TOML_UNLIKELY(decoder_.error())
365                     {
366                         calc_positions();
367                         utf8_reader_error("Encountered invalid utf-8 sequence", error_pos(), source_path_);
368                         utf8_reader_return_after_error(false);
369                     }
370 
371                     currently_decoding_.bytes[currently_decoding_.count++] = raw_bytes[i];
372 
373                     if (decoder_.has_code_point())
374                     {
375                         auto& cp = codepoints_.buffer[codepoints_.count++];
376 
377                         cp.value = decoder_.codepoint;
378                         cp.count = currently_decoding_.count;
379                         std::memcpy(cp.bytes, currently_decoding_.bytes, currently_decoding_.count);
380                         currently_decoding_.count = {};
381                     }
382                     else if TOML_UNLIKELY(currently_decoding_.count == 4u)
383                     {
384                         calc_positions();
385                         utf8_reader_error("Encountered overlong utf-8 sequence", error_pos(), source_path_);
386                         utf8_reader_return_after_error(false);
387                     }
388                 }
389                 if TOML_UNLIKELY(decoder_.needs_more_input() && stream_.eof())
390                 {
391                     calc_positions();
392                     utf8_reader_error("Encountered EOF during incomplete utf-8 code point sequence",
393                                       error_pos(),
394                                       source_path_);
395                     utf8_reader_return_after_error(false);
396                 }
397             }
398 
399             TOML_ASSERT_ASSUME(codepoints_.count);
400             calc_positions();
401 
402             // handle general I/O errors
403             // (down here so the next_pos_ benefits from calc_positions())
404             if TOML_UNLIKELY(stream_.error())
405             {
406                 utf8_reader_error("An I/O error occurred while reading from the underlying stream",
407                                   next_pos_,
408                                   source_path_);
409                 utf8_reader_return_after_error(false);
410             }
411 
412             return true;
413         }
414 
415       public:
416         template <typename U, typename String = std::string_view>
417         TOML_NODISCARD_CTOR
418         explicit utf8_reader(U&& source, String&& source_path = {}) noexcept(
419             std::is_nothrow_constructible_v<utf8_byte_stream<T>, U&&>)
420             : stream_{ static_cast<U&&>(source) }
421         {
422             currently_decoding_.count = {};
423 
424             codepoints_.current = {};
425             codepoints_.count   = {};
426 
427             if (!source_path.empty())
428                 source_path_ = std::make_shared<const std::string>(static_cast<String&&>(source_path));
429         }
430 
431         TOML_PURE_INLINE_GETTER
432         const source_path_ptr& source_path() const noexcept final
433         {
434             return source_path_;
435         }
436 
437         TOML_NODISCARD
438         const utf8_codepoint* read_next() noexcept(!TOML_COMPILER_HAS_EXCEPTIONS) final
439         {
440             utf8_reader_error_check({});
441 
442             if (codepoints_.current == codepoints_.count)
443             {
444                 if TOML_UNLIKELY(!stream_ || !read_next_block())
445                     return nullptr;
446 
447                 TOML_ASSERT_ASSUME(!codepoints_.current);
448             }
449             TOML_ASSERT_ASSUME(codepoints_.count);
450             TOML_ASSERT_ASSUME(codepoints_.count <= block_capacity);
451             TOML_ASSERT_ASSUME(codepoints_.current < codepoints_.count);
452 
453             return &codepoints_.buffer[codepoints_.current++];
454         }
455 
456         TOML_NODISCARD
457         bool peek_eof() const noexcept(!TOML_COMPILER_HAS_EXCEPTIONS) final
458         {
459             return stream_.peek_eof();
460         }
461 
462 #if !TOML_EXCEPTIONS
463 
464         TOML_NODISCARD
465         optional<parse_error>&& error() noexcept final
466         {
467             return std::move(err_);
468         }
469 
470 #endif
471     };
472 
473     template <typename Char>
474     utf8_reader(std::basic_string_view<Char>, std::string_view) -> utf8_reader<std::basic_string_view<Char>>;
475     template <typename Char>
476     utf8_reader(std::basic_string_view<Char>, std::string &&) -> utf8_reader<std::basic_string_view<Char>>;
477     template <typename Char>
478     utf8_reader(std::basic_istream<Char>&, std::string_view) -> utf8_reader<std::basic_istream<Char>>;
479     template <typename Char>
480     utf8_reader(std::basic_istream<Char>&, std::string &&) -> utf8_reader<std::basic_istream<Char>>;
481 
482 #if TOML_EXCEPTIONS
483 #define utf8_buffered_reader_error_check(...) static_assert(true)
484 #else
485 #define utf8_buffered_reader_error_check(...)                                                                          \
486     do                                                                                                                 \
487     {                                                                                                                  \
488         if TOML_UNLIKELY(reader_.error())                                                                              \
489             return __VA_ARGS__;                                                                                        \
490     }                                                                                                                  \
491     while (false)
492 
493 #endif
494 
495     class TOML_EMPTY_BASES utf8_buffered_reader
496     {
497       public:
498         static constexpr size_t max_history_length = 128;
499 
500       private:
501         static constexpr size_t history_buffer_size = max_history_length - 1; //'head' is stored in the reader
502         utf8_reader_interface& reader_;
503         struct
504         {
505             utf8_codepoint buffer[history_buffer_size];
506             size_t count, first;
507         } history_                  = {};
508         const utf8_codepoint* head_ = {};
509         size_t negative_offset_     = {};
510 
511       public:
512         TOML_NODISCARD_CTOR
513         explicit utf8_buffered_reader(utf8_reader_interface& reader) noexcept //
514             : reader_{ reader }
515         {}
516 
517         TOML_PURE_INLINE_GETTER
518         const source_path_ptr& source_path() const noexcept
519         {
520             return reader_.source_path();
521         }
522 
523         TOML_NODISCARD
524         const utf8_codepoint* read_next() noexcept(!TOML_COMPILER_HAS_EXCEPTIONS)
525         {
526             utf8_buffered_reader_error_check({});
527 
528             if (negative_offset_)
529             {
530                 negative_offset_--;
531 
532                 // an entry negative offset of 1 just means "replay the current head"
533                 if (!negative_offset_)
534                     return head_;
535 
536                 // otherwise step back into the history buffer
537                 else
538                     return history_.buffer
539                          + ((history_.first + history_.count - negative_offset_) % history_buffer_size);
540             }
541             else
542             {
543                 // first character read from stream
544                 if TOML_UNLIKELY(!history_.count && !head_)
545                     head_ = reader_.read_next();
546 
547                 // subsequent characters and not eof
548                 else if (head_)
549                 {
550                     if TOML_UNLIKELY(history_.count < history_buffer_size)
551                         history_.buffer[history_.count++] = *head_;
552                     else
553                         history_.buffer[(history_.first++ + history_buffer_size) % history_buffer_size] = *head_;
554 
555                     head_ = reader_.read_next();
556                 }
557 
558                 return head_;
559             }
560         }
561 
562         TOML_NODISCARD
563         const utf8_codepoint* step_back(size_t count) noexcept
564         {
565             utf8_buffered_reader_error_check({});
566 
567             TOML_ASSERT_ASSUME(history_.count);
568             TOML_ASSERT_ASSUME(negative_offset_ + count <= history_.count);
569 
570             negative_offset_ += count;
571 
572             return negative_offset_
573                      ? history_.buffer + ((history_.first + history_.count - negative_offset_) % history_buffer_size)
574                      : head_;
575         }
576 
577         TOML_NODISCARD
578         bool peek_eof() const noexcept(!TOML_COMPILER_HAS_EXCEPTIONS)
579         {
580             return reader_.peek_eof();
581         }
582 
583 #if !TOML_EXCEPTIONS
584 
585         TOML_NODISCARD
586         optional<parse_error>&& error() noexcept
587         {
588             return reader_.error();
589         }
590 
591 #endif
592     };
593 }
