676     TOML_CONST_GETTER
677     TOML_INTERNAL_LINKAGE
678     std::string_view to_sv(bool val) noexcept
679     {
680         using namespace std::string_view_literals;
681 
682         return val ? "true"sv : "false"sv;
683     }
684 
685     TOML_PURE_GETTER
686     TOML_INTERNAL_LINKAGE
687     std::string_view to_sv(const utf8_codepoint& cp) noexcept
688     {
689         if (cp.value <= U'\x1F')
690             return impl::control_char_escapes[cp.value];
691         else if (cp.value == U'\x7F')
692             return "\\u007F"sv;
693         else
694             return std::string_view{ cp.bytes, cp.count };
695     }
696 
697     TOML_PURE_GETTER
698     TOML_INTERNAL_LINKAGE
699     std::string_view to_sv(const utf8_codepoint* cp) noexcept
700     {
701         if (cp)
702             return to_sv(*cp);
703         return ""sv;
704     }
