933     struct table_vector_scope
934     {
935         std::vector<table*>& tables;
936 
937         TOML_NODISCARD_CTOR
938         explicit table_vector_scope(std::vector<table*>& tables_, table& tbl) //
939             : tables{ tables_ }
940         {
941             tables.push_back(&tbl);
942         }
943 
944         ~table_vector_scope() noexcept
945         {
946             tables.pop_back();
947         }
948 
949         TOML_DELETE_DEFAULTS(table_vector_scope);
950     };
951 }
952 TOML_ANON_NAMESPACE_END;
953 
954 #if 1 // parser helper macros
955 
956 // Q: "what the fuck is this? MACROS????"
957 // A: The parser needs to work in exceptionless mode (returning error objects directly)
958 //    and exception mode (reporting parse failures by throwing). Two totally different control flows.
959 //    These macros encapsulate the differences between the two modes so I can write code code
960 //    as though I was only targeting one mode and not want yeet myself into the sun.
961 //    They're all #undef'd at the bottom of the parser's implementation so they should be harmless outside
962 //    of toml++.
963 
964 #define is_eof()         !cp
965 #define assert_not_eof() TOML_ASSERT_ASSUME(cp != nullptr)
966 #define return_if_eof(...)                                                                                             \
967     do                                                                                                                 \
968     {                                                                                                                  \
969         if TOML_UNLIKELY(is_eof())                                                                                     \
970             return __VA_ARGS__;                                                                                        \
971     }                                                                                                                  \
972     while (false)
973 
974 #if TOML_EXCEPTIONS
975 #define is_error()                  false
976 #define return_after_error(...)     TOML_UNREACHABLE
977 #define assert_not_error()          static_assert(true)
978 #define return_if_error(...)        static_assert(true)
979 #define return_if_error_or_eof(...) return_if_eof(__VA_ARGS__)
980 #else
981 #define is_error()              !!err
982 #define return_after_error(...) return __VA_ARGS__
983 #define assert_not_error()      TOML_ASSERT(!is_error())
984 #define return_if_error(...)                                                                                           \
985     do                                                                                                                 \
986     {                                                                                                                  \
987         if TOML_UNLIKELY(is_error())                                                                                   \
988             return __VA_ARGS__;                                                                                        \
989     }                                                                                                                  \
990     while (false)
991 #define return_if_error_or_eof(...)                                                                                    \
992     do                                                                                                                 \
993     {                                                                                                                  \
994         if TOML_UNLIKELY(is_eof() || is_error())                                                                       \
995             return __VA_ARGS__;                                                                                        \
996     }                                                                                                                  \
997     while (false)
998 #endif
999 
1000 #if defined(TOML_BREAK_AT_PARSE_ERRORS) && TOML_BREAK_AT_PARSE_ERRORS
1001 #if defined(__has_builtin)
1002 #if __has_builtin(__builtin_debugtrap)
1003 #define parse_error_break() __builtin_debugtrap()
1004 #elif __has_builtin(__debugbreak)
1005 #define parse_error_break() __debugbreak()
1006 #endif
1007 #endif
1008 #ifndef parse_error_break
1009 #if TOML_MSVC || TOML_ICC
1010 #define parse_error_break() __debugbreak()
1011 #else
1012 #define parse_error_break() TOML_ASSERT(false)
1013 #endif
1014 #endif
1015 #else
1016 #define parse_error_break() static_assert(true)
1017 #endif
1018 
1019 #define set_error_and_return(ret, ...)                                                                                 \
1020     do                                                                                                                 \
1021     {                                                                                                                  \
1022         if (!is_error())                                                                                               \
1023             set_error(__VA_ARGS__);                                                                                    \
1024         return_after_error(ret);                                                                                       \
1025     }                                                                                                                  \
1026     while (false)
1027 
1028 #define set_error_and_return_default(...) set_error_and_return({}, __VA_ARGS__)
1029 
1030 #define set_error_and_return_if_eof(...)                                                                               \
1031     do                                                                                                                 \
1032     {                                                                                                                  \
1033         if TOML_UNLIKELY(is_eof())                                                                                     \
1034             set_error_and_return(__VA_ARGS__, "encountered end-of-file"sv);                                            \
1035     }                                                                                                                  \
1036     while (false)
1037 
1038 #define advance_and_return_if_error(...)                                                                               \
1039     do                                                                                                                 \
1040     {                                                                                                                  \
1041         assert_not_eof();                                                                                              \
1042         advance();                                                                                                     \
1043         return_if_error(__VA_ARGS__);                                                                                  \
1044     }                                                                                                                  \
1045     while (false)
1046 
1047 #define advance_and_return_if_error_or_eof(...)                                                                        \
1048     do                                                                                                                 \
1049     {                                                                                                                  \
1050         assert_not_eof();                                                                                              \
1051         advance();                                                                                                     \
1052         return_if_error(__VA_ARGS__);                                                                                  \
1053         set_error_and_return_if_eof(__VA_ARGS__);                                                                      \
1054     }                                                                                                                  \
1055     while (false)
1056 
1057 #endif // parser helper macros
1058 
1059 TOML_IMPL_NAMESPACE_START
1060 {
1061     TOML_ABI_NAMESPACE_BOOL(TOML_EXCEPTIONS, impl_ex, impl_noex);
1062 
1063     class parser
1064     {
1065       private:
1066         static constexpr size_t max_nested_values = TOML_MAX_NESTED_VALUES;
1067 
1068         utf8_buffered_reader reader;
1069         table root;
1070         source_position prev_pos = { 1, 1 };
1071         const utf8_codepoint* cp = {};
1072         std::vector<table*> implicit_tables;
1073         std::vector<table*> dotted_key_tables;
1074         std::vector<table*> open_inline_tables;
1075         std::vector<array*> table_arrays;
1076         parse_key_buffer key_buffer;
1077         std::string string_buffer;
1078         std::string recording_buffer; // for diagnostics
1079         bool recording = false, recording_whitespace = true;
1080         std::string_view current_scope;
1081         size_t nested_values = {};
1082 #if !TOML_EXCEPTIONS
1083         mutable optional<parse_error> err;
1084 #endif
1085 
1086         TOML_NODISCARD
1087         source_position current_position(source_index fallback_offset = 0) const noexcept
1088         {
1089             if (!is_eof())
1090                 return cp->position;
1091             return { prev_pos.line, static_cast<source_index>(prev_pos.column + fallback_offset) };
1092         }
1093 
1094         template <typename... T>
1095         TOML_RETURNS_BY_THROWING
1096         TOML_NEVER_INLINE
1097         void set_error_at(source_position pos, const T&... reason) const
1098         {
1099             static_assert(sizeof...(T) > 0);
1100             return_if_error();
1101 
1102             error_builder builder{ current_scope };
1103             (builder.append(reason), ...);
1104 
1105             parse_error_break();
1106 
1107 #if TOML_EXCEPTIONS
1108             builder.finish(pos, reader.source_path());
1109 #else
1110             err.emplace(builder.finish(pos, reader.source_path()));
1111 #endif
1112         }
1113 
1114         template <typename... T>
1115         TOML_RETURNS_BY_THROWING
1116         void set_error(const T&... reason) const
1117         {
1118             set_error_at(current_position(1), reason...);
1119         }
1120 
1121         void go_back(size_t count = 1) noexcept
1122         {
1123             return_if_error();
1124             TOML_ASSERT_ASSUME(count);
1125 
1126             cp       = reader.step_back(count);
1127             prev_pos = cp->position;
1128         }
1129 
1130         void advance()
1131         {
1132             return_if_error();
1133             assert_not_eof();
1134 
1135             prev_pos = cp->position;
1136             cp       = reader.read_next();
1137 
1138 #if !TOML_EXCEPTIONS
1139             if (reader.error())
1140             {
1141                 err = std::move(reader.error());
1142                 return;
1143             }
1144 #endif
1145 
1146             if (recording && !is_eof())
1147             {
1148                 if (recording_whitespace || !is_whitespace(*cp))
1149                     recording_buffer.append(cp->bytes, cp->count);
1150             }
1151         }
1152 
1153         void start_recording(bool include_current = true) noexcept
1154         {
1155             return_if_error();
1156 
1157             recording            = true;
1158             recording_whitespace = true;
1159             recording_buffer.clear();
1160             if (include_current && !is_eof())
1161                 recording_buffer.append(cp->bytes, cp->count);
1162         }
1163 
1164         void stop_recording(size_t pop_bytes = 0) noexcept
1165         {
1166             return_if_error();
1167 
1168             recording = false;
1169             if (pop_bytes)
1170             {
1171                 if (pop_bytes >= recording_buffer.length())
1172                     recording_buffer.clear();
1173                 else if (pop_bytes == 1u)
1174                     recording_buffer.pop_back();
1175                 else
1176                     recording_buffer.erase(recording_buffer.begin()
1177                                                + static_cast<ptrdiff_t>(recording_buffer.length() - pop_bytes),
1178                                            recording_buffer.end());
1179             }
1180         }
1181 
1182         bool consume_leading_whitespace()
1183         {
1184             return_if_error_or_eof({});
1185 
1186             bool consumed = false;
1187             while (!is_eof() && is_horizontal_whitespace(*cp))
1188             {
1189                 if TOML_UNLIKELY(!is_ascii_horizontal_whitespace(*cp))
1190                     set_error_and_return_default("expected space or tab, saw '"sv, escaped_codepoint{ *cp }, "'"sv);
1191 
1192                 consumed = true;
1193                 advance_and_return_if_error({});
1194             }
1195             return consumed;
1196         }
1197 
1198         bool consume_line_break()
1199         {
1200             return_if_error_or_eof({});
1201 
1202             if TOML_UNLIKELY(is_match(*cp, U'\v', U'\f'))
1203                 set_error_and_return_default(
1204                     R"(vertical tabs '\v' and form-feeds '\f' are not legal line breaks in TOML)"sv);
1205 
1206             if (*cp == U'\r')
1207             {
1208                 advance_and_return_if_error({}); // skip \r
1209 
1210                 if TOML_UNLIKELY(is_eof())
1211                     set_error_and_return_default("expected '\\n' after '\\r', saw EOF"sv);
1212 
1213                 if TOML_UNLIKELY(*cp != U'\n')
1214                     set_error_and_return_default("expected '\\n' after '\\r', saw '"sv,
1215                                                  escaped_codepoint{ *cp },
1216                                                  "'"sv);
1217             }
1218             else if (*cp != U'\n')
1219                 return false;
1220 
1221             advance_and_return_if_error({}); // skip \n
1222             return true;
1223         }
1224 
1225         bool consume_rest_of_line()
1226         {
1227             return_if_error_or_eof({});
1228 
1229             do
1230             {
1231                 if (is_ascii_vertical_whitespace(*cp))
1232                     return consume_line_break();
1233                 else
1234                     advance();
1235                 return_if_error({});
1236             }
1237             while (!is_eof());
1238 
1239             return true;
1240         }
1241 
1242         bool consume_comment()
1243         {
1244             return_if_error_or_eof({});
1245 
1246             if (*cp != U'#')
1247                 return false;
1248 
1249             push_parse_scope("comment"sv);
1250 
1251             advance_and_return_if_error({}); // skip the '#'
1252 
1253             while (!is_eof())
1254             {
1255                 if (consume_line_break())
1256                     return true;
1257                 return_if_error({});
1258 
1259 #if TOML_LANG_AT_LEAST(1, 0, 0)
1260 
1261                 // toml/issues/567 (disallow non-TAB control characters in comments)
1262                 if TOML_UNLIKELY(is_nontab_control_character(*cp))
1263                     set_error_and_return_default(
1264                         "control characters other than TAB (U+0009) are explicitly prohibited in comments"sv);
1265 
1266                 // toml/pull/720 (disallow surrogates in comments)
1267                 else if TOML_UNLIKELY(is_unicode_surrogate(*cp))
1268                     set_error_and_return_default(
1269                         "unicode surrogates (U+D800 to U+DFFF) are explicitly prohibited in comments"sv);
1270 #endif
1271 
1272                 advance_and_return_if_error({});
1273             }
1274 
1275             return true;
1276         }
1277 
1278         TOML_NODISCARD
1279         bool consume_expected_sequence(std::u32string_view seq)
1280         {
1281             return_if_error({});
1282             TOML_ASSERT(!seq.empty());
1283 
1284             for (auto c : seq)
1285             {
1286                 set_error_and_return_if_eof({});
1287                 if (*cp != c)
1288                     return false;
1289                 advance_and_return_if_error({});
1290             }
1291             return true;
1292         }
1293 
1294         template <typename T>
1295         TOML_NODISCARD
1296         bool consume_digit_sequence(T* digits, size_t len)
1297         {
1298             return_if_error({});
1299             TOML_ASSERT_ASSUME(digits);
1300             TOML_ASSERT_ASSUME(len);
1301 
1302             for (size_t i = 0; i < len; i++)
1303             {
1304                 set_error_and_return_if_eof({});
1305                 if (!is_decimal_digit(*cp))
1306                     return false;
1307 
1308                 digits[i] = static_cast<T>(*cp - U'0');
1309                 advance_and_return_if_error({});
1310             }
1311             return true;
1312         }
1313 
1314         template <typename T>
1315         TOML_NODISCARD
1316         size_t consume_variable_length_digit_sequence(T* buffer, size_t max_len)
1317         {
1318             return_if_error({});
1319             TOML_ASSERT_ASSUME(buffer);
1320             TOML_ASSERT_ASSUME(max_len);
1321 
1322             size_t i = {};
1323             for (; i < max_len; i++)
1324             {
1325                 if (is_eof() || !is_decimal_digit(*cp))
1326                     break;
1327 
1328                 buffer[i] = static_cast<T>(*cp - U'0');
1329                 advance_and_return_if_error({});
1330             }
1331             return i;
1332         }
1333 
1334         TOML_NODISCARD
1335         TOML_NEVER_INLINE
1336         std::string_view parse_basic_string(bool multi_line)
1337         {
1338             return_if_error({});
1339             assert_not_eof();
1340             TOML_ASSERT_ASSUME(*cp == U'"');
1341             push_parse_scope("string"sv);
1342 
1343             // skip the '"'
1344             advance_and_return_if_error_or_eof({});
1345 
1346             // multi-line strings ignore a single line ending right at the beginning
1347             if (multi_line)
1348             {
1349                 consume_line_break();
1350                 return_if_error({});
1351                 set_error_and_return_if_eof({});
1352             }
1353 
1354             auto& str = string_buffer;
1355             str.clear();
1356             bool escaped             = false;
1357             bool skipping_whitespace = false;
1358             do
1359             {
1360                 if (escaped)
1361                 {
1362                     escaped = false;
1363 
1364                     // handle 'line ending slashes' in multi-line mode
1365                     if (multi_line && is_whitespace(*cp))
1366                     {
1367                         consume_leading_whitespace();
1368 
1369                         if TOML_UNLIKELY(!consume_line_break())
1370                             set_error_and_return_default(
1371                                 "line-ending backslashes must be the last non-whitespace character on the line"sv);
1372 
1373                         skipping_whitespace = true;
1374                         return_if_error({});
1375                         continue;
1376                     }
1377 
1378                     bool skip_escaped_codepoint = true;
1379                     assert_not_eof();
1380                     switch (const auto escaped_codepoint = *cp)
1381                     {
1382                         // 'regular' escape codes
1383                         case U'b': str += '\b'; break;
1384                         case U'f': str += '\f'; break;
1385                         case U'n': str += '\n'; break;
1386                         case U'r': str += '\r'; break;
1387                         case U't': str += '\t'; break;
1388                         case U'"': str += '"'; break;
1389                         case U'\\': str += '\\'; break;
1390 
1391 #if TOML_LANG_UNRELEASED // toml/pull/790 (\e shorthand for \x1B)
1392                         case U'e': str += '\x1B'; break;
1393 #else
1394                         case U'e':
1395                             set_error_and_return_default(
1396                                 "escape sequence '\\e' is not supported in TOML 1.0.0 and earlier"sv);
1397 #endif
1398 
1399 #if TOML_LANG_UNRELEASED // toml/pull/796 (\xHH unicode scalar sequences)
1400                         case U'x': [[fallthrough]];
1401 #else
1402                         case U'x':
1403                             set_error_and_return_default(
1404                                 "escape sequence '\\x' is not supported in TOML 1.0.0 and earlier"sv);
1405 #endif
1406 
1407                         // unicode scalar sequences
1408                         case U'u': [[fallthrough]];
1409                         case U'U':
1410                         {
1411                             push_parse_scope("unicode scalar sequence"sv);
1412                             advance_and_return_if_error_or_eof({});
1413                             skip_escaped_codepoint = false;
1414 
1415                             uint32_t place_value =
1416                                 escaped_codepoint == U'U' ? 0x10000000u : (escaped_codepoint == U'u' ? 0x1000u : 0x10u);
1417                             uint32_t sequence_value{};
1418                             while (place_value)
1419                             {
1420                                 set_error_and_return_if_eof({});
1421 
1422                                 if TOML_UNLIKELY(!is_hexadecimal_digit(*cp))
1423                                     set_error_and_return_default("expected hex digit, saw '"sv, to_sv(*cp), "'"sv);
1424 
1425                                 sequence_value += place_value * hex_to_dec(*cp);
1426                                 place_value /= 16u;
1427                                 advance_and_return_if_error({});
1428                             }
1429 
1430                             if TOML_UNLIKELY(is_unicode_surrogate(sequence_value))
1431                                 set_error_and_return_default(
1432                                     "unicode surrogates (U+D800 - U+DFFF) are explicitly prohibited"sv);
1433                             else if TOML_UNLIKELY(sequence_value > 0x10FFFFu)
1434                                 set_error_and_return_default("values greater than U+10FFFF are invalid"sv);
1435 
1436                             if (sequence_value < 0x80)
1437                             {
1438                                 str += static_cast<char>(sequence_value);
1439                             }
1440                             else if (sequence_value < 0x800u)
1441                             {
1442                                 str += static_cast<char>((sequence_value >> 6) | 0xC0u);
1443                                 str += static_cast<char>((sequence_value & 0x3Fu) | 0x80u);
1444                             }
1445                             else if (sequence_value < 0x10000u)
1446                             {
1447                                 str += static_cast<char>((sequence_value >> 12) | 0xE0u);
1448                                 str += static_cast<char>(((sequence_value >> 6) & 0x3Fu) | 0x80u);
1449                                 str += static_cast<char>((sequence_value & 0x3Fu) | 0x80u);
1450                             }
1451                             else if (sequence_value < 0x110000u)
1452                             {
1453                                 str += static_cast<char>((sequence_value >> 18) | 0xF0u);
1454                                 str += static_cast<char>(((sequence_value >> 12) & 0x3Fu) | 0x80u);
1455                                 str += static_cast<char>(((sequence_value >> 6) & 0x3Fu) | 0x80u);
1456                                 str += static_cast<char>((sequence_value & 0x3Fu) | 0x80u);
1457                             }
1458                             break;
1459                         }
1460 
1461                             // ???
1462                             TOML_UNLIKELY_CASE
1463                         default: set_error_and_return_default("unknown escape sequence '\\"sv, to_sv(*cp), "'"sv);
1464                     }
1465 
1466                     if (skip_escaped_codepoint)
1467                         advance_and_return_if_error_or_eof({});
1468                 }
1469                 else
1470                 {
1471                     // handle closing delimiters
1472                     if (*cp == U'"')
1473                     {
1474                         if (multi_line)
1475                         {
1476                             size_t lookaheads             = {};
1477                             size_t consecutive_delimiters = 1;
1478                             do
1479                             {
1480                                 advance_and_return_if_error({});
1481                                 lookaheads++;
1482                                 if (!is_eof() && *cp == U'"')
1483                                     consecutive_delimiters++;
1484                                 else
1485                                     break;
1486                             }
1487                             while (lookaheads < 4u);
1488 
1489                             switch (consecutive_delimiters)
1490                             {
1491                                 // """ " (one quote somewhere in a ML string)
1492                                 case 1:
1493                                     str += '"';
1494                                     skipping_whitespace = false;
1495                                     continue;
1496 
1497                                 // """ "" (two quotes somewhere in a ML string)
1498                                 case 2:
1499                                     str.append("\"\""sv);
1500                                     skipping_whitespace = false;
1501                                     continue;
1502 
1503                                 // """ """ (the end of the string)
1504                                 case 3: return str;
1505 
1506                                 // """ """" (one at the end of the string)
1507                                 case 4: str += '"'; return str;
1508 
1509                                 // """ """"" (two quotes at the end of the string)
1510                                 case 5:
1511                                     str.append("\"\""sv);
1512                                     advance_and_return_if_error({}); // skip the last '"'
1513                                     return str;
1514 
1515                                 default: TOML_UNREACHABLE;
1516                             }
1517                         }
1518                         else
1519                         {
1520                             advance_and_return_if_error({}); // skip the closing delimiter
1521                             return str;
1522                         }
1523                     }
1524 
1525                     // handle escapes
1526                     else if (*cp == U'\\')
1527                     {
1528                         advance_and_return_if_error_or_eof({}); // skip the '\'
1529                         skipping_whitespace = false;
1530                         escaped             = true;
1531                         continue;
1532                     }
1533 
1534                     // handle line endings in multi-line mode
1535                     if (multi_line && is_ascii_vertical_whitespace(*cp))
1536                     {
1537                         consume_line_break();
1538                         return_if_error({});
1539                         if (!skipping_whitespace)
1540                             str += '\n';
1541                         continue;
1542                     }
1543 
1544                     // handle control characters
1545                     if TOML_UNLIKELY(is_nontab_control_character(*cp))
1546                         set_error_and_return_default(
1547                             "unescaped control characters other than TAB (U+0009) are explicitly prohibited"sv);
1548 
1549 #if TOML_LANG_AT_LEAST(1, 0, 0)
1550 
1551                     // handle surrogates in strings
1552                     if TOML_UNLIKELY(is_unicode_surrogate(*cp))
1553                         set_error_and_return_default(
1554                             "unescaped unicode surrogates (U+D800 to U+DFFF) are explicitly prohibited"sv);
1555 #endif
1556 
1557                     if (multi_line)
1558                     {
1559                         if (!skipping_whitespace || !is_horizontal_whitespace(*cp))
1560                         {
1561                             skipping_whitespace = false;
1562                             str.append(cp->bytes, cp->count);
1563                         }
1564                     }
1565                     else
1566                         str.append(cp->bytes, cp->count);
1567 
1568                     advance_and_return_if_error({});
1569                 }
1570             }
1571             while (!is_eof());
1572 
1573             set_error_and_return_default("encountered end-of-file"sv);
1574         }
1575 
1576         TOML_NODISCARD
1577         TOML_NEVER_INLINE
1578         std::string_view parse_literal_string(bool multi_line)
1579         {
1580             return_if_error({});
1581             assert_not_eof();
1582             TOML_ASSERT_ASSUME(*cp == U'\'');
1583             push_parse_scope("literal string"sv);
1584 
1585             // skip the delimiter
1586             advance_and_return_if_error_or_eof({});
1587 
1588             // multi-line strings ignore a single line ending right at the beginning
1589             if (multi_line)
1590             {
1591                 consume_line_break();
1592                 return_if_error({});
1593                 set_error_and_return_if_eof({});
1594             }
1595 
1596             auto& str = string_buffer;
1597             str.clear();
1598             do
1599             {
1600                 return_if_error({});
1601 
1602                 // handle closing delimiters
1603                 if (*cp == U'\'')
1604                 {
1605                     if (multi_line)
1606                     {
1607                         size_t lookaheads             = {};
1608                         size_t consecutive_delimiters = 1;
1609                         do
1610                         {
1611                             advance_and_return_if_error({});
1612                             lookaheads++;
1613                             if (!is_eof() && *cp == U'\'')
1614                                 consecutive_delimiters++;
1615                             else
1616                                 break;
1617                         }
1618                         while (lookaheads < 4u);
1619 
1620                         switch (consecutive_delimiters)
1621                         {
1622                             // ''' ' (one quote somewhere in a ML string)
1623                             case 1: str += '\''; continue;
1624 
1625                             // ''' '' (two quotes somewhere in a ML string)
1626                             case 2: str.append("''"sv); continue;
1627 
1628                             // ''' ''' (the end of the string)
1629                             case 3: return str;
1630 
1631                             // ''' '''' (one at the end of the string)
1632                             case 4: str += '\''; return str;
1633 
1634                             // ''' ''''' (two quotes at the end of the string)
1635                             case 5:
1636                                 str.append("''"sv);
1637                                 advance_and_return_if_error({}); // skip the last '
1638                                 return str;
1639 
1640                             default: TOML_UNREACHABLE;
1641                         }
1642                     }
1643                     else
1644                     {
1645                         advance_and_return_if_error({}); // skip the closing delimiter
1646                         return str;
1647                     }
1648                 }
1649 
1650                 // handle line endings in multi-line mode
1651                 if (multi_line && is_ascii_vertical_whitespace(*cp))
1652                 {
1653                     consume_line_break();
1654                     return_if_error({});
1655                     str += '\n';
1656                     continue;
1657                 }
1658 
1659                 // handle control characters
1660                 if TOML_UNLIKELY(is_nontab_control_character(*cp))
1661                     set_error_and_return_default(
1662                         "control characters other than TAB (U+0009) are explicitly prohibited"sv);
1663 
1664 #if TOML_LANG_AT_LEAST(1, 0, 0)
1665 
1666                 // handle surrogates in strings
1667                 if TOML_UNLIKELY(is_unicode_surrogate(*cp))
1668                     set_error_and_return_default("unicode surrogates (U+D800 - U+DFFF) are explicitly prohibited"sv);
1669 #endif
1670 
1671                 str.append(cp->bytes, cp->count);
1672                 advance_and_return_if_error({});
1673             }
1674             while (!is_eof());
1675 
1676             set_error_and_return_default("encountered end-of-file"sv);
1677         }
1678 
1679         TOML_NODISCARD
1680         TOML_NEVER_INLINE
1681         parsed_string parse_string()
1682         {
1683             return_if_error({});
1684             assert_not_eof();
1685             TOML_ASSERT_ASSUME(is_string_delimiter(*cp));
1686             push_parse_scope("string"sv);
1687 
1688             // get the first three characters to determine the string type
1689             const auto first = cp->value;
1690             advance_and_return_if_error_or_eof({});
1691             const auto second = cp->value;
1692             advance_and_return_if_error({});
1693             const auto third = cp ? cp->value : U'\0';
1694 
1695             // if we were eof at the third character then first and second need to be
1696             // the same string character (otherwise it's an unterminated string)
1697             if (is_eof())
1698             {
1699                 if (second == first)
1700                     return {};
1701 
1702                 set_error_and_return_default("encountered end-of-file"sv);
1703             }
1704 
1705             // if the first three characters are all the same string delimiter then
1706             // it's a multi-line string.
1707             else if (first == second && first == third)
1708             {
1709                 return { first == U'\'' ? parse_literal_string(true) : parse_basic_string(true), true };
1710             }
1711 
1712             // otherwise it's just a regular string.
1713             else
1714             {
1715                 // step back two characters so that the current
1716                 // character is the string delimiter
1717                 go_back(2u);
1718 
1719                 return { first == U'\'' ? parse_literal_string(false) : parse_basic_string(false), false };
1720             }
1721         }
1722 
1723         TOML_NODISCARD
1724         TOML_NEVER_INLINE
1725         std::string_view parse_bare_key_segment()
1726         {
1727             return_if_error({});
1728             assert_not_eof();
1729             TOML_ASSERT_ASSUME(is_bare_key_character(*cp));
1730 
1731             string_buffer.clear();
1732 
1733             while (!is_eof())
1734             {
1735                 if (!is_bare_key_character(*cp))
1736                     break;
1737 
1738                 string_buffer.append(cp->bytes, cp->count);
1739                 advance_and_return_if_error({});
1740             }
1741 
1742             return string_buffer;
1743         }
1744 
1745         TOML_NODISCARD
1746         TOML_NEVER_INLINE
1747         bool parse_boolean()
1748         {
1749             return_if_error({});
1750             assert_not_eof();
1751             TOML_ASSERT_ASSUME(is_match(*cp, U't', U'f', U'T', U'F'));
1752             push_parse_scope("boolean"sv);
1753 
1754             start_recording(true);
1755             auto result = is_match(*cp, U't', U'T');
1756             if (!consume_expected_sequence(result ? U"true"sv : U"false"sv))
1757                 set_error_and_return_default("expected '"sv,
1758                                              to_sv(result),
1759                                              "', saw '"sv,
1760                                              to_sv(recording_buffer),
1761                                              "'"sv);
1762             stop_recording();
1763 
1764             if (cp && !is_value_terminator(*cp))
1765                 set_error_and_return_default("expected value-terminator, saw '"sv, to_sv(*cp), "'"sv);
1766 
1767             return result;
1768         }
