1770         TOML_NODISCARD
1771         TOML_NEVER_INLINE
1772         double parse_inf_or_nan()
1773         {
1774             return_if_error({});
1775             assert_not_eof();
1776             TOML_ASSERT_ASSUME(is_match(*cp, U'i', U'n', U'I', U'N', U'+', U'-'));
1777             push_parse_scope("floating-point"sv);
1778 
1779             start_recording(true);
1780             const bool negative = *cp == U'-';
1781             if (negative || *cp == U'+')
1782                 advance_and_return_if_error_or_eof({});
1783 
1784             const bool inf = is_match(*cp, U'i', U'I');
1785             if (!consume_expected_sequence(inf ? U"inf"sv : U"nan"sv))
1786                 set_error_and_return_default("expected '"sv,
1787                                              inf ? "inf"sv : "nan"sv,
1788                                              "', saw '"sv,
1789                                              to_sv(recording_buffer),
1790                                              "'"sv);
1791             stop_recording();
1792 
1793             if (cp && !is_value_terminator(*cp))
1794                 set_error_and_return_default("expected value-terminator, saw '"sv, to_sv(*cp), "'"sv);
1795 
1796             return inf ? (negative ? -std::numeric_limits<double>::infinity() : std::numeric_limits<double>::infinity())
1797                        : std::numeric_limits<double>::quiet_NaN();
1798         }
1799 
1800         TOML_NODISCARD
1801         TOML_NEVER_INLINE
1802         double parse_float()
1803         {
1804             return_if_error({});
1805             assert_not_eof();
1806             TOML_ASSERT_ASSUME(is_match(*cp, U'+', U'-', U'.') || is_decimal_digit(*cp));
1807             push_parse_scope("floating-point"sv);
1808 
1809             // sign
1810             const int sign = *cp == U'-' ? -1 : 1;
1811             if (is_match(*cp, U'+', U'-'))
1812                 advance_and_return_if_error_or_eof({});
1813 
1814             // consume value chars
1815             char chars[utf8_buffered_reader::max_history_length];
1816             size_t length              = {};
1817             const utf8_codepoint* prev = {};
1818             bool seen_decimal = false, seen_exponent = false;
1819             char first_integer_part = '\0';
1820             while (!is_eof() && !is_value_terminator(*cp))
1821             {
1822                 if (*cp == U'_')
1823                 {
1824                     if (!prev || !is_decimal_digit(*prev))
1825                         set_error_and_return_default("underscores may only follow digits"sv);
1826 
1827                     prev = cp;
1828                     advance_and_return_if_error_or_eof({});
1829                     continue;
1830                 }
1831                 else if TOML_UNLIKELY(prev && *prev == U'_' && !is_decimal_digit(*cp))
1832                     set_error_and_return_default("underscores must be followed by digits"sv);
1833                 else if TOML_UNLIKELY(length == sizeof(chars))
1834                     set_error_and_return_default("exceeds length limit of "sv,
1835                                                  sizeof(chars),
1836                                                  " digits"sv,
1837                                                  (seen_exponent ? ""sv : " (consider using exponent notation)"sv));
1838                 else if (*cp == U'.')
1839                 {
1840                     // .1
1841                     // -.1
1842                     // +.1 (no integer part)
1843                     if (!first_integer_part)
1844                         set_error_and_return_default("expected decimal digit, saw '.'"sv);
1845 
1846                     // 1.0e+.10 (exponent cannot have '.')
1847                     else if (seen_exponent)
1848                         set_error_and_return_default("expected exponent decimal digit or sign, saw '.'"sv);
1849 
1850                     // 1.0.e+.10
1851                     // 1..0
1852                     // (multiple '.')
1853                     else if (seen_decimal)
1854                         set_error_and_return_default("expected decimal digit or exponent, saw '.'"sv);
1855 
1856                     seen_decimal = true;
1857                 }
1858                 else if (is_match(*cp, U'e', U'E'))
1859                 {
1860                     if (prev && !is_decimal_digit(*prev))
1861                         set_error_and_return_default("expected decimal digit, saw '"sv, to_sv(*cp), "'"sv);
1862 
1863                     // 1.0ee+10 (multiple 'e')
1864                     else if (seen_exponent)
1865                         set_error_and_return_default("expected decimal digit, saw '"sv, to_sv(*cp), "'"sv);
1866 
1867                     seen_decimal  = true; // implied
1868                     seen_exponent = true;
1869                 }
1870                 else if (is_match(*cp, U'+', U'-'))
1871                 {
1872                     // 1.-0 (sign in mantissa)
1873                     if (!seen_exponent)
1874                         set_error_and_return_default("expected decimal digit or '.', saw '"sv, to_sv(*cp), "'"sv);
1875 
1876                     // 1.0e1-0 (misplaced exponent sign)
1877                     else if (!is_match(*prev, U'e', U'E'))
1878                         set_error_and_return_default("expected exponent digit, saw '"sv, to_sv(*cp), "'"sv);
1879                 }
1880                 else if (is_decimal_digit(*cp))
1881                 {
1882                     if (!seen_decimal)
1883                     {
1884                         if (!first_integer_part)
1885                             first_integer_part = static_cast<char>(cp->bytes[0]);
1886                         else if (first_integer_part == '0')
1887                             set_error_and_return_default("leading zeroes are prohibited"sv);
1888                     }
1889                 }
1890                 else
1891                     set_error_and_return_default("expected decimal digit, saw '"sv, to_sv(*cp), "'"sv);
1892 
1893                 chars[length++] = static_cast<char>(cp->bytes[0]);
1894                 prev            = cp;
1895                 advance_and_return_if_error({});
1896             }
1897 
1898             // sanity-check ending state
1899             if (prev)
1900             {
1901                 if (*prev == U'_')
1902                 {
1903                     set_error_and_return_if_eof({});
1904                     set_error_and_return_default("underscores must be followed by digits"sv);
1905                 }
1906                 else if (is_match(*prev, U'e', U'E', U'+', U'-', U'.'))
1907                 {
1908                     set_error_and_return_if_eof({});
1909                     set_error_and_return_default("expected decimal digit, saw '"sv, to_sv(*cp), "'"sv);
1910                 }
1911             }
1912 
1913             // convert to double
1914             double result;
1915 #if TOML_FLOAT_CHARCONV
1916             {
1917                 auto fc_result = std::from_chars(chars, chars + length, result);
1918                 switch (fc_result.ec)
1919                 {
1920                     TOML_LIKELY_CASE
1921                     case std::errc{}: // ok
1922                         return result * sign;
1923 
1924                     case std::errc::invalid_argument:
1925                         set_error_and_return_default("'"sv,
1926                                                      std::string_view{ chars, length },
1927                                                      "' could not be interpreted as a value"sv);
1928                         break;
1929 
1930                     case std::errc::result_out_of_range:
1931                         set_error_and_return_default("'"sv,
1932                                                      std::string_view{ chars, length },
1933                                                      "' is not representable in 64 bits"sv);
1934                         break;
1935 
1936                     default: //??
1937                         set_error_and_return_default("an unspecified error occurred while trying to interpret '"sv,
1938                                                      std::string_view{ chars, length },
1939                                                      "' as a value"sv);
1940                 }
1941             }
1942 #else
1943             {
1944                 std::stringstream ss;
1945                 ss.imbue(std::locale::classic());
1946                 ss.write(chars, static_cast<std::streamsize>(length));
1947                 if ((ss >> result))
1948                     return result * sign;
1949                 else
1950                     set_error_and_return_default("'"sv,
1951                                                  std::string_view{ chars, length },
1952                                                  "' could not be interpreted as a value"sv);
1953             }
1954 #endif
1955         }
1956 
1957         TOML_NODISCARD
1958         TOML_NEVER_INLINE
1959         double parse_hex_float()
1960         {
1961             return_if_error({});
1962             assert_not_eof();
1963             TOML_ASSERT_ASSUME(is_match(*cp, U'0', U'+', U'-'));
1964             push_parse_scope("hexadecimal floating-point"sv);
1965 
1966 #if TOML_LANG_UNRELEASED // toml/issues/562 (hexfloats)
1967 
1968             // sign
1969             const int sign = *cp == U'-' ? -1 : 1;
1970             if (is_match(*cp, U'+', U'-'))
1971                 advance_and_return_if_error_or_eof({});
1972 
1973             // '0'
1974             if (*cp != U'0')
1975                 set_error_and_return_default(" expected '0', saw '"sv, to_sv(*cp), "'"sv);
1976             advance_and_return_if_error_or_eof({});
1977 
1978             // 'x' or 'X'
1979             if (!is_match(*cp, U'x', U'X'))
1980                 set_error_and_return_default("expected 'x' or 'X', saw '"sv, to_sv(*cp), "'"sv);
1981             advance_and_return_if_error_or_eof({});
1982 
1983             // <HEX DIGITS> ([.]<HEX DIGITS>)? [pP] [+-]? <DEC DIGITS>
1984 
1985             // consume value fragments
1986             struct fragment
1987             {
1988                 char chars[24];
1989                 size_t length;
1990                 double value;
1991             };
1992             fragment fragments[] = {
1993                 {}, // mantissa, whole part
1994                 {}, // mantissa, fractional part
1995                 {}  // exponent
1996             };
1997             fragment* current_fragment = fragments;
1998             const utf8_codepoint* prev = {};
1999             int exponent_sign          = 1;
2000             while (!is_eof() && !is_value_terminator(*cp))
2001             {
2002                 if (*cp == U'_')
2003                 {
2004                     if (!prev || !is_hexadecimal_digit(*prev))
2005                         set_error_and_return_default("underscores may only follow digits"sv);
2006 
2007                     prev = cp;
2008                     advance_and_return_if_error_or_eof({});
2009                     continue;
2010                 }
2011                 else if (prev && *prev == U'_' && !is_hexadecimal_digit(*cp))
2012                     set_error_and_return_default("underscores must be followed by digits"sv);
2013                 else if (*cp == U'.')
2014                 {
2015                     // 0x10.0p-.0 (exponent cannot have '.')
2016                     if (current_fragment == fragments + 2)
2017                         set_error_and_return_default("expected exponent digit or sign, saw '.'"sv);
2018 
2019                     // 0x10.0.p-0 (multiple '.')
2020                     else if (current_fragment == fragments + 1)
2021                         set_error_and_return_default("expected hexadecimal digit or exponent, saw '.'"sv);
2022 
2023                     else
2024                         current_fragment++;
2025                 }
2026                 else if (is_match(*cp, U'p', U'P'))
2027                 {
2028                     // 0x10.0pp-0 (multiple 'p')
2029                     if (current_fragment == fragments + 2)
2030                         set_error_and_return_default("expected exponent digit or sign, saw '"sv, to_sv(*cp), "'"sv);
2031 
2032                     // 0x.p-0 (mantissa is just '.')
2033                     else if (fragments[0].length == 0u && fragments[1].length == 0u)
2034                         set_error_and_return_default("expected hexadecimal digit, saw '"sv, to_sv(*cp), "'"sv);
2035 
2036                     else
2037                         current_fragment = fragments + 2;
2038                 }
2039                 else if (is_match(*cp, U'+', U'-'))
2040                 {
2041                     // 0x-10.0p-0 (sign in mantissa)
2042                     if (current_fragment != fragments + 2)
2043                         set_error_and_return_default("expected hexadecimal digit or '.', saw '"sv, to_sv(*cp), "'"sv);
2044 
2045                     // 0x10.0p0- (misplaced exponent sign)
2046                     else if (!is_match(*prev, U'p', U'P'))
2047                         set_error_and_return_default("expected exponent digit, saw '"sv, to_sv(*cp), "'"sv);
2048 
2049                     else
2050                         exponent_sign = *cp == U'-' ? -1 : 1;
2051                 }
2052                 else if (current_fragment < fragments + 2 && !is_hexadecimal_digit(*cp))
2053                     set_error_and_return_default("expected hexadecimal digit or '.', saw '"sv, to_sv(*cp), "'"sv);
2054                 else if (current_fragment == fragments + 2 && !is_decimal_digit(*cp))
2055                     set_error_and_return_default("expected exponent digit or sign, saw '"sv, to_sv(*cp), "'"sv);
2056                 else if (current_fragment->length == sizeof(fragment::chars))
2057                     set_error_and_return_default("fragment exceeeds maximum length of "sv,
2058                                                  sizeof(fragment::chars),
2059                                                  " characters"sv);
2060                 else
2061                     current_fragment->chars[current_fragment->length++] = static_cast<char>(cp->bytes[0]);
2062 
2063                 prev = cp;
2064                 advance_and_return_if_error({});
2065             }
2066 
2067             // sanity-check ending state
2068             if (current_fragment != fragments + 2 || current_fragment->length == 0u)
2069             {
2070                 set_error_and_return_if_eof({});
2071                 set_error_and_return_default("missing exponent"sv);
2072             }
2073             else if (prev && *prev == U'_')
2074             {
2075                 set_error_and_return_if_eof({});
2076                 set_error_and_return_default("underscores must be followed by digits"sv);
2077             }
2078 
2079             // calculate values for the three fragments
2080             for (int fragment_idx = 0; fragment_idx < 3; fragment_idx++)
2081             {
2082                 auto& f             = fragments[fragment_idx];
2083                 const uint32_t base = fragment_idx == 2 ? 10u : 16u;
2084 
2085                 // left-trim zeroes
2086                 const char* c = f.chars;
2087                 size_t sig    = {};
2088                 while (f.length && *c == '0')
2089                 {
2090                     f.length--;
2091                     c++;
2092                     sig++;
2093                 }
2094                 if (!f.length)
2095                     continue;
2096 
2097                 // calculate value
2098                 auto place = 1u;
2099                 for (size_t i = 0; i < f.length - 1u; i++)
2100                     place *= base;
2101                 uint32_t val{};
2102                 while (place)
2103                 {
2104                     if (base == 16)
2105                         val += place * hex_to_dec(*c);
2106                     else
2107                         val += place * static_cast<uint32_t>(*c - '0');
2108                     if (fragment_idx == 1)
2109                         sig++;
2110                     c++;
2111                     place /= base;
2112                 }
2113                 f.value = static_cast<double>(val);
2114 
2115                 // shift the fractional part
2116                 if (fragment_idx == 1)
2117                 {
2118                     while (sig--)
2119                         f.value /= base;
2120                 }
2121             }
2122 
2123             return (fragments[0].value + fragments[1].value) * pow(2.0, fragments[2].value * exponent_sign) * sign;
2124 
2125 #else // !TOML_LANG_UNRELEASED
2126 
2127             set_error_and_return_default("hexadecimal floating-point values are not supported "
2128                                          "in TOML 1.0.0 and earlier"sv);
2129 
2130 #endif // !TOML_LANG_UNRELEASED
2131         }
2132 
2133         template <uint64_t base>
2134         TOML_NODISCARD
2135         TOML_NEVER_INLINE
2136         int64_t parse_integer()
2137         {
2138             return_if_error({});
2139             assert_not_eof();
2140             using traits = parse_integer_traits<base>;
2141             push_parse_scope(traits::scope_qualifier);
2142 
2143             [[maybe_unused]] int64_t sign = 1;
2144             if constexpr (traits::is_signed)
2145             {
2146                 sign = *cp == U'-' ? -1 : 1;
2147                 if (is_match(*cp, U'+', U'-'))
2148                     advance_and_return_if_error_or_eof({});
2149             }
2150 
2151             if constexpr (base == 10)
2152             {
2153                 if (!traits::is_digit(*cp))
2154                     set_error_and_return_default("expected expected digit or sign, saw '"sv, to_sv(*cp), "'"sv);
2155             }
2156             else
2157             {
2158                 // '0'
2159                 if (*cp != U'0')
2160                     set_error_and_return_default("expected '0', saw '"sv, to_sv(*cp), "'"sv);
2161                 advance_and_return_if_error_or_eof({});
2162 
2163                 // 'b', 'o', 'x'
2164                 if (*cp != traits::prefix_codepoint)
2165                     set_error_and_return_default("expected '"sv, traits::prefix, "', saw '"sv, to_sv(*cp), "'"sv);
2166                 advance_and_return_if_error_or_eof({});
2167 
2168                 if (!traits::is_digit(*cp))
2169                     set_error_and_return_default("expected digit, saw '"sv, to_sv(*cp), "'"sv);
2170             }
2171 
2172             // consume digits
2173             char digits[utf8_buffered_reader::max_history_length];
2174             size_t length              = {};
2175             const utf8_codepoint* prev = {};
2176             while (!is_eof() && !is_value_terminator(*cp))
2177             {
2178                 if (*cp == U'_')
2179                 {
2180                     if (!prev || !traits::is_digit(*prev))
2181                         set_error_and_return_default("underscores may only follow digits"sv);
2182 
2183                     prev = cp;
2184                     advance_and_return_if_error_or_eof({});
2185                     continue;
2186                 }
2187                 else if TOML_UNLIKELY(prev && *prev == U'_' && !traits::is_digit(*cp))
2188                     set_error_and_return_default("underscores must be followed by digits"sv);
2189                 else if TOML_UNLIKELY(!traits::is_digit(*cp))
2190                     set_error_and_return_default("expected digit, saw '"sv, to_sv(*cp), "'"sv);
2191                 else if TOML_UNLIKELY(length == sizeof(digits))
2192                     set_error_and_return_default("exceeds length limit of "sv, sizeof(digits), " digits"sv);
2193                 else
2194                     digits[length++] = static_cast<char>(cp->bytes[0]);
2195 
2196                 prev = cp;
2197                 advance_and_return_if_error({});
2198             }
2199 
2200             // sanity check ending state
2201             if (prev && *prev == U'_')
2202             {
2203                 set_error_and_return_if_eof({});
2204                 set_error_and_return_default("underscores must be followed by digits"sv);
2205             }
2206 
2207             // single digits can be converted trivially
2208             if (length == 1u)
2209             {
2210                 int64_t result;
2211 
2212                 if constexpr (base == 16)
2213                     result = static_cast<int64_t>(hex_to_dec(digits[0]));
2214                 else
2215                     result = static_cast<int64_t>(digits[0] - '0');
2216 
2217                 if constexpr (traits::is_signed)
2218                     result *= sign;
2219 
2220                 return result;
2221             }
2222 
2223             // bin, oct and hex allow leading zeroes so trim them first
2224             const char* end = digits + length;
2225             const char* msd = digits;
2226             if constexpr (base != 10)
2227             {
2228                 while (msd < end && *msd == '0')
2229                     msd++;
2230                 if (msd == end)
2231                     return 0ll;
2232             }
2233 
2234             // decimal integers do not allow leading zeroes
2235             else
2236             {
2237                 if TOML_UNLIKELY(digits[0] == '0')
2238                     set_error_and_return_default("leading zeroes are prohibited"sv);
2239             }
2240 
2241             // range check
2242             if TOML_UNLIKELY(static_cast<size_t>(end - msd) > traits::max_digits)
2243                 set_error_and_return_default("'"sv,
2244                                              traits::full_prefix,
2245                                              std::string_view{ digits, length },
2246                                              "' is not representable in 64 bits"sv);
2247 
2248             // do the thing
2249             {
2250                 uint64_t result = {};
2251                 {
2252                     uint64_t power = 1;
2253                     while (--end >= msd)
2254                     {
2255                         if constexpr (base == 16)
2256                             result += power * hex_to_dec(*end);
2257                         else
2258                             result += power * static_cast<uint64_t>(*end - '0');
2259 
2260                         power *= base;
2261                     }
2262                 }
2263 
2264                 // range check
2265                 static constexpr auto i64_max = static_cast<uint64_t>((std::numeric_limits<int64_t>::max)());
2266                 if TOML_UNLIKELY(result > i64_max + (sign < 0 ? 1u : 0u))
2267                     set_error_and_return_default("'"sv,
2268                                                  traits::full_prefix,
2269                                                  std::string_view{ digits, length },
2270                                                  "' is not representable in 64 bits"sv);
2271 
2272                 if constexpr (traits::is_signed)
2273                 {
2274                     // avoid signed multiply UB when parsing INT64_MIN
2275                     if TOML_UNLIKELY(sign < 0 && result == i64_max + 1u)
2276                         return (std::numeric_limits<int64_t>::min)();
2277 
2278                     return static_cast<int64_t>(result) * sign;
2279                 }
2280                 else
2281                     return static_cast<int64_t>(result);
2282             }
2283         }
2284 
2285         TOML_NODISCARD
2286         TOML_NEVER_INLINE
2287         date parse_date(bool part_of_datetime = false)
2288         {
2289             return_if_error({});
2290             assert_not_eof();
2291             TOML_ASSERT_ASSUME(is_decimal_digit(*cp));
2292             push_parse_scope("date"sv);
2293 
2294             // "YYYY"
2295             uint32_t digits[4];
2296             if (!consume_digit_sequence(digits, 4u))
2297                 set_error_and_return_default("expected 4-digit year, saw '"sv, to_sv(cp), "'"sv);
2298             const auto year         = digits[3] + digits[2] * 10u + digits[1] * 100u + digits[0] * 1000u;
2299             const auto is_leap_year = (year % 4u == 0u) && ((year % 100u != 0u) || (year % 400u == 0u));
2300             set_error_and_return_if_eof({});
2301 
2302             // '-'
2303             if (*cp != U'-')
2304                 set_error_and_return_default("expected '-', saw '"sv, to_sv(*cp), "'"sv);
2305             advance_and_return_if_error_or_eof({});
2306 
2307             // "MM"
2308             if (!consume_digit_sequence(digits, 2u))
2309                 set_error_and_return_default("expected 2-digit month, saw '"sv, to_sv(cp), "'"sv);
2310             const auto month = digits[1] + digits[0] * 10u;
2311             if (month == 0u || month > 12u)
2312                 set_error_and_return_default("expected month between 1 and 12 (inclusive), saw "sv, month);
2313             const auto max_days_in_month = month == 2u
2314                                              ? (is_leap_year ? 29u : 28u)
2315                                              : (month == 4u || month == 6u || month == 9u || month == 11u ? 30u : 31u);
2316             set_error_and_return_if_eof({});
2317 
2318             // '-'
2319             if (*cp != U'-')
2320                 set_error_and_return_default("expected '-', saw '"sv, to_sv(*cp), "'"sv);
2321             advance_and_return_if_error_or_eof({});
2322 
2323             // "DD"
2324             if (!consume_digit_sequence(digits, 2u))
2325                 set_error_and_return_default("expected 2-digit day, saw '"sv, to_sv(cp), "'"sv);
2326             const auto day = digits[1] + digits[0] * 10u;
2327             if (day == 0u || day > max_days_in_month)
2328                 set_error_and_return_default("expected day between 1 and "sv,
2329                                              max_days_in_month,
2330                                              " (inclusive), saw "sv,
2331                                              day);
2332 
2333             if (!part_of_datetime && !is_eof() && !is_value_terminator(*cp))
2334                 set_error_and_return_default("expected value-terminator, saw '"sv, to_sv(*cp), "'"sv);
2335 
2336             return { year, month, day };
2337         }
2338 
2339         TOML_NODISCARD
2340         TOML_NEVER_INLINE
2341         time parse_time(bool part_of_datetime = false)
2342         {
2343             return_if_error({});
2344             assert_not_eof();
2345             TOML_ASSERT_ASSUME(is_decimal_digit(*cp));
2346             push_parse_scope("time"sv);
2347 
2348             static constexpr size_t max_digits = 64; // far more than necessary but needed to allow fractional
2349                                                      // millisecond truncation per the spec
2350             uint32_t digits[max_digits];
2351 
2352             // "HH"
2353             if (!consume_digit_sequence(digits, 2u))
2354                 set_error_and_return_default("expected 2-digit hour, saw '"sv, to_sv(cp), "'"sv);
2355             const auto hour = digits[1] + digits[0] * 10u;
2356             if (hour > 23u)
2357                 set_error_and_return_default("expected hour between 0 to 59 (inclusive), saw "sv, hour);
2358             set_error_and_return_if_eof({});
2359 
2360             // ':'
2361             if (*cp != U':')
2362                 set_error_and_return_default("expected ':', saw '"sv, to_sv(*cp), "'"sv);
2363             advance_and_return_if_error_or_eof({});
2364 
2365             // "MM"
2366             if (!consume_digit_sequence(digits, 2u))
2367                 set_error_and_return_default("expected 2-digit minute, saw '"sv, to_sv(cp), "'"sv);
2368             const auto minute = digits[1] + digits[0] * 10u;
2369             if (minute > 59u)
2370                 set_error_and_return_default("expected minute between 0 and 59 (inclusive), saw "sv, minute);
2371             auto time = toml::time{ hour, minute };
2372 
2373             // ':'
2374             if constexpr (TOML_LANG_UNRELEASED) // toml/issues/671 (allow omission of seconds)
2375             {
2376                 if (is_eof() || is_value_terminator(*cp) || (part_of_datetime && is_match(*cp, U'+', U'-', U'Z', U'z')))
2377                     return time;
2378             }
2379             else
2380                 set_error_and_return_if_eof({});
2381             if (*cp != U':')
2382                 set_error_and_return_default("expected ':', saw '"sv, to_sv(*cp), "'"sv);
2383             advance_and_return_if_error_or_eof({});
2384 
2385             // "SS"
2386             if (!consume_digit_sequence(digits, 2u))
2387                 set_error_and_return_default("expected 2-digit second, saw '"sv, to_sv(cp), "'"sv);
2388             const auto second = digits[1] + digits[0] * 10u;
2389             if (second > 59u)
2390                 set_error_and_return_default("expected second between 0 and 59 (inclusive), saw "sv, second);
2391             time.second = static_cast<decltype(time.second)>(second);
2392 
2393             // '.' (early-exiting is allowed; fractional is optional)
2394             if (is_eof() || is_value_terminator(*cp) || (part_of_datetime && is_match(*cp, U'+', U'-', U'Z', U'z')))
2395                 return time;
2396             if (*cp != U'.')
2397                 set_error_and_return_default("expected '.', saw '"sv, to_sv(*cp), "'"sv);
2398             advance_and_return_if_error_or_eof({});
2399 
2400             // "FFFFFFFFF"
2401             size_t digit_count = consume_variable_length_digit_sequence(digits, max_digits);
2402             if (!digit_count)
2403             {
2404                 set_error_and_return_if_eof({});
2405                 set_error_and_return_default("expected fractional digits, saw '"sv, to_sv(*cp), "'"sv);
2406             }
2407             else if (!is_eof())
2408             {
2409                 if (digit_count == max_digits && is_decimal_digit(*cp))
2410                     set_error_and_return_default("fractional component exceeds maximum precision of "sv, max_digits);
2411                 else if (!part_of_datetime && !is_value_terminator(*cp))
2412                     set_error_and_return_default("expected value-terminator, saw '"sv, to_sv(*cp), "'"sv);
2413             }
2414             uint32_t value = 0u;
2415             uint32_t place = 1u;
2416             for (auto i = impl::min<size_t>(digit_count, 9u); i-- > 0u;)
2417             {
2418                 value += digits[i] * place;
2419                 place *= 10u;
2420             }
2421             for (auto i = digit_count; i < 9u; i++) // implicit zeros
2422                 value *= 10u;
2423             time.nanosecond = value;
2424             return time;
2425         }
2426 
2427         TOML_NODISCARD
2428         TOML_NEVER_INLINE
2429         date_time parse_date_time()
2430         {
2431             return_if_error({});
2432             assert_not_eof();
2433             TOML_ASSERT_ASSUME(is_decimal_digit(*cp));
2434             push_parse_scope("date-time"sv);
2435 
2436             // "YYYY-MM-DD"
2437             auto date = parse_date(true);
2438             set_error_and_return_if_eof({});
2439 
2440             // ' ', 'T' or 't'
2441             if (!is_match(*cp, U' ', U'T', U't'))
2442                 set_error_and_return_default("expected space, 'T' or 't', saw '"sv, to_sv(*cp), "'"sv);
2443             advance_and_return_if_error_or_eof({});
2444 
2445             // "HH:MM:SS.FFFFFFFFF"
2446             auto time = parse_time(true);
2447             return_if_error({});
2448 
2449             // no offset
2450             if (is_eof() || is_value_terminator(*cp))
2451                 return { date, time };
2452 
2453             // zero offset ('Z' or 'z')
2454             time_offset offset{};
2455             if (is_match(*cp, U'Z', U'z'))
2456                 advance_and_return_if_error({});
2457 
2458             // explicit offset ("+/-HH:MM")
2459             else if (is_match(*cp, U'+', U'-'))
2460             {
2461                 push_parse_scope("date-time offset"sv);
2462 
2463                 // sign
2464                 int sign = *cp == U'-' ? -1 : 1;
2465                 advance_and_return_if_error_or_eof({});
2466 
2467                 // "HH"
2468                 int digits[2];
2469                 if (!consume_digit_sequence(digits, 2u))
2470                     set_error_and_return_default("expected 2-digit hour, saw '"sv, to_sv(cp), "'"sv);
2471                 const auto hour = digits[1] + digits[0] * 10;
2472                 if (hour > 23)
2473                     set_error_and_return_default("expected hour between 0 and 23 (inclusive), saw "sv, hour);
2474                 set_error_and_return_if_eof({});
2475 
2476                 // ':'
2477                 if (*cp != U':')
2478                     set_error_and_return_default("expected ':', saw '"sv, to_sv(*cp), "'"sv);
2479                 advance_and_return_if_error_or_eof({});
2480 
2481                 // "MM"
2482                 if (!consume_digit_sequence(digits, 2u))
2483                     set_error_and_return_default("expected 2-digit minute, saw '"sv, to_sv(cp), "'"sv);
2484                 const auto minute = digits[1] + digits[0] * 10;
2485                 if (minute > 59)
2486                     set_error_and_return_default("expected minute between 0 and 59 (inclusive), saw "sv, minute);
2487                 offset.minutes = static_cast<decltype(offset.minutes)>((hour * 60 + minute) * sign);
2488             }
2489 
2490             if (!is_eof() && !is_value_terminator(*cp))
2491                 set_error_and_return_default("expected value-terminator, saw '"sv, to_sv(*cp), "'"sv);
2492 
2493             return { date, time, offset };
2494         }
2495 
2496         TOML_NODISCARD
2497         node_ptr parse_array();
2498 
2499         TOML_NODISCARD
2500         node_ptr parse_inline_table();
2501 
2502         TOML_NODISCARD
2503         node_ptr parse_value_known_prefixes()
2504         {
2505             return_if_error({});
2506             assert_not_eof();
2507             TOML_ASSERT_ASSUME(!is_control_character(*cp));
2508             TOML_ASSERT_ASSUME(*cp != U'_');
2509 
2510             switch (cp->value)
2511             {
2512                 // arrays
2513                 case U'[': return parse_array();
2514 
2515                 // inline tables
2516                 case U'{': return parse_inline_table();
2517 
2518                 // floats beginning with '.'
2519                 case U'.': return node_ptr{ new value{ parse_float() } };
2520 
2521                 // strings
2522                 case U'"': [[fallthrough]];
2523                 case U'\'': return node_ptr{ new value{ parse_string().value } };
2524 
2525                 default:
2526                 {
2527                     const auto cp_upper = static_cast<uint_least32_t>(cp->value) & ~0x20u;
2528 
2529                     // bools
2530                     if (cp_upper == 70u || cp_upper == 84u) // F or T
2531                         return node_ptr{ new value{ parse_boolean() } };
2532 
2533                     // inf/nan
2534                     else if (cp_upper == 73u || cp_upper == 78u) // I or N
2535                         return node_ptr{ new value{ parse_inf_or_nan() } };
2536 
2537                     else
2538                         return nullptr;
2539                 }
2540             }
2541             TOML_UNREACHABLE;
2542         }
2543 
2544         TOML_NODISCARD
2545         node_ptr parse_value()
2546         {
2547             return_if_error({});
2548             assert_not_eof();
2549             TOML_ASSERT_ASSUME(!is_value_terminator(*cp));
2550             push_parse_scope("value"sv);
2551 
2552             const depth_counter_scope depth_counter{ nested_values };
2553             if TOML_UNLIKELY(nested_values > max_nested_values)
2554                 set_error_and_return_default("exceeded maximum nested value depth of "sv,
2555                                              max_nested_values,
2556                                              " (TOML_MAX_NESTED_VALUES)"sv);
2557 
2558             // check if it begins with some control character
2559             // (note that this will also fail for whitespace but we're assuming we've
2560             // called consume_leading_whitespace() before calling parse_value())
2561             if TOML_UNLIKELY(is_control_character(*cp))
2562                 set_error_and_return_default("unexpected control character"sv);
2563 
2564             // underscores at the beginning
2565             else if (*cp == U'_')
2566                 set_error_and_return_default("values may not begin with underscores"sv);
2567 
2568             const auto begin_pos = cp->position;
2569             node_ptr val;
2570 
2571             do
2572             {
2573                 TOML_ASSERT_ASSUME(!is_control_character(*cp));
2574                 TOML_ASSERT_ASSUME(*cp != U'_');
2575 
2576                 // detect the value type and parse accordingly,
2577                 // starting with value types that can be detected
2578                 // unambiguously from just one character.
2579 
2580                 val = parse_value_known_prefixes();
2581                 return_if_error({});
2582                 if (val)
2583                     break;
2584 
2585                 // value types from here down require more than one character to unambiguously identify
2586                 // so scan ahead and collect a set of value 'traits'.
2587                 enum TOML_CLOSED_FLAGS_ENUM value_traits : int
2588                 {
2589                     has_nothing  = 0,
2590                     has_digits   = 1,
2591                     has_b        = 1 << 1, // as second char only (0b)
2592                     has_e        = 1 << 2, // only float exponents
2593                     has_o        = 1 << 3, // as second char only (0o)
2594                     has_p        = 1 << 4, // only hexfloat exponents
2595                     has_t        = 1 << 5,
2596                     has_x        = 1 << 6, // as second or third char only (0x, -0x, +0x)
2597                     has_z        = 1 << 7,
2598                     has_colon    = 1 << 8,
2599                     has_plus     = 1 << 9,
2600                     has_minus    = 1 << 10,
2601                     has_dot      = 1 << 11,
2602                     begins_sign  = 1 << 12,
2603                     begins_digit = 1 << 13,
2604                     begins_zero  = 1 << 14,
2605 
2606                     signs_msk  = has_plus | has_minus,
2607                     bdigit_msk = has_digits | begins_digit,
2608                     bzero_msk  = bdigit_msk | begins_zero,
2609                 };
2610                 value_traits traits  = has_nothing;
2611                 const auto has_any   = [&](auto t) noexcept { return (traits & t) != has_nothing; };
2612                 const auto has_none  = [&](auto t) noexcept { return (traits & t) == has_nothing; };
2613                 const auto add_trait = [&](auto t) noexcept { traits = static_cast<value_traits>(traits | t); };
2614 
2615                 // examine the first character to get the 'begins with' traits
2616                 // (good fail-fast opportunity; all the remaining types begin with numeric digits or signs)
2617                 if (is_decimal_digit(*cp))
2618                 {
2619                     add_trait(begins_digit);
2620                     if (*cp == U'0')
2621                         add_trait(begins_zero);
2622                 }
2623                 else if (is_match(*cp, U'+', U'-'))
2624                     add_trait(begins_sign);
2625                 else
2626                     break;
2627 
2628                 // scan the rest of the value to determine the remaining traits
2629                 char32_t chars[utf8_buffered_reader::max_history_length];
2630                 size_t char_count = {}, advance_count = {};
2631                 bool eof_while_scanning = false;
2632                 const auto scan         = [&]() noexcept(!TOML_COMPILER_HAS_EXCEPTIONS)
2633                 {
2634                     if (is_eof())
2635                         return;
2636                     TOML_ASSERT_ASSUME(!is_value_terminator(*cp));
2637 
2638                     do
2639                     {
2640                         if (const auto c = **cp; c != U'_')
2641                         {
2642                             chars[char_count++] = c;
2643 
2644                             if (is_decimal_digit(c))
2645                                 add_trait(has_digits);
2646                             else if (is_ascii_letter(c))
2647                             {
2648                                 TOML_ASSERT_ASSUME((c >= U'a' && c <= U'z') || (c >= U'A' && c <= U'Z'));
2649                                 switch (static_cast<char32_t>(c | 32u))
2650                                 {
2651                                     case U'b':
2652                                         if (char_count == 2u && has_any(begins_zero))
2653                                             add_trait(has_b);
2654                                         break;
2655 
2656                                     case U'e':
2657                                         if (char_count > 1u
2658                                             && has_none(has_b | has_o | has_p | has_t | has_x | has_z | has_colon)
2659                                             && (has_none(has_plus | has_minus) || has_any(begins_sign)))
2660                                             add_trait(has_e);
2661                                         break;
2662 
2663                                     case U'o':
2664                                         if (char_count == 2u && has_any(begins_zero))
2665                                             add_trait(has_o);
2666                                         break;
2667 
2668                                     case U'p':
2669                                         if (has_any(has_x))
2670                                             add_trait(has_p);
2671                                         break;
2672 
2673                                     case U'x':
2674                                         if ((char_count == 2u && has_any(begins_zero))
2675                                             || (char_count == 3u && has_any(begins_sign) && chars[1] == U'0'))
2676                                             add_trait(has_x);
2677                                         break;
2678 
2679                                     case U't': add_trait(has_t); break;
2680                                     case U'z': add_trait(has_z); break;
2681                                 }
2682                             }
2683                             else if (c <= U':')
2684                             {
2685                                 TOML_ASSERT_ASSUME(c < U'0' || c > U'9');
2686                                 switch (c)
2687                                 {
2688                                     case U'+': add_trait(has_plus); break;
2689                                     case U'-': add_trait(has_minus); break;
2690                                     case U'.': add_trait(has_dot); break;
2691                                     case U':': add_trait(has_colon); break;
2692                                 }
2693                             }
2694                         }
2695 
2696                         advance_and_return_if_error();
2697                         advance_count++;
2698                         eof_while_scanning = is_eof();
2699                     }
2700                     while (advance_count < (utf8_buffered_reader::max_history_length - 1u) && !is_eof()
2701                            && !is_value_terminator(*cp));
2702                 };
2703                 scan();
2704                 return_if_error({});
2705 
2706                 // force further scanning if this could have been a date-time with a space instead of a T
2707                 if (char_count == 10u                                    //
2708                     && (traits | begins_zero) == (bzero_msk | has_minus) //
2709                     && chars[4] == U'-'                                  //
2710                     && chars[7] == U'-'                                  //
2711                     && !is_eof()                                         //
2712                     && *cp == U' ')
2713                 {
2714                     const auto pre_advance_count = advance_count;
2715                     const auto pre_scan_traits   = traits;
2716                     chars[char_count++]          = *cp;
2717                     add_trait(has_t);
2718 
2719                     const auto backpedal = [&]() noexcept
2720                     {
2721                         go_back(advance_count - pre_advance_count);
2722                         advance_count = pre_advance_count;
2723                         traits        = pre_scan_traits;
2724                         char_count    = 10u;
2725                     };
2726 
2727                     advance_and_return_if_error({});
2728                     advance_count++;
2729 
2730                     if (is_eof() || !is_decimal_digit(*cp))
2731                         backpedal();
2732                     else
2733                     {
2734                         chars[char_count++] = *cp;
2735 
2736                         advance_and_return_if_error({});
2737                         advance_count++;
2738 
2739                         scan();
2740                         return_if_error({});
2741 
2742                         if (char_count == 12u)
2743                             backpedal();
2744                     }
2745                 }
2746 
2747                 // set the reader back to where we started
2748                 go_back(advance_count);
2749 
2750                 // if after scanning ahead we still only have one value character,
2751                 // the only valid value type is an integer.
2752                 if (char_count == 1u)
2753                 {
2754                     if (has_any(begins_digit))
2755                     {
2756                         val.reset(new value{ static_cast<int64_t>(chars[0] - U'0') });
2757                         advance(); // skip the digit
2758                         break;
2759                     }
2760 
2761                     // anything else would be ambiguous.
2762                     else
2763                         set_error_and_return_default(eof_while_scanning ? "encountered end-of-file"sv
2764                                                                         : "could not determine value type"sv);
2765                 }
2766 
2767                 // now things that can be identified from two or more characters
2768                 return_if_error({});
2769                 TOML_ASSERT_ASSUME(char_count >= 2u);
2770 
2771                 // do some 'fuzzy matching' where there's no ambiguity, since that allows the specific
2772                 // typed parse functions to take over and show better diagnostics if there's an issue
2773                 // (as opposed to the fallback "could not determine type" message)
2774                 if (has_any(has_p))
2775                     val.reset(new value{ parse_hex_float() });
2776                 else if (has_any(has_x | has_o | has_b))
2777                 {
2778                     int64_t i;
2779                     value_flags flags;
2780                     if (has_any(has_x))
2781                     {
2782                         i     = parse_integer<16>();
2783                         flags = value_flags::format_as_hexadecimal;
2784                     }
2785                     else if (has_any(has_o))
2786                     {
2787                         i     = parse_integer<8>();
2788                         flags = value_flags::format_as_octal;
2789                     }
2790                     else // has_b
2791                     {
2792                         i     = parse_integer<2>();
2793                         flags = value_flags::format_as_binary;
2794                     }
2795                     return_if_error({});
2796 
2797                     val.reset(new value{ i });
2798                     val->ref_cast<int64_t>().flags(flags);
2799                 }
2800                 else if (has_any(has_e) || (has_any(begins_digit) && chars[1] == U'.'))
2801                     val.reset(new value{ parse_float() });
2802                 else if (has_any(begins_sign))
2803                 {
2804                     // single-digit signed integers
2805                     if (char_count == 2u && has_any(has_digits))
2806                     {
2807                         val.reset(new value{ static_cast<int64_t>(chars[1] - U'0') * (chars[0] == U'-' ? -1LL : 1LL) });
2808                         advance(); // skip the sign
2809                         advance(); // skip the digit
2810                         break;
2811                     }
2812 
2813                     // simple signed floats (e.g. +1.0)
2814                     if (is_decimal_digit(chars[1]) && chars[2] == U'.')
2815                         val.reset(new value{ parse_float() });
2816 
2817                     // signed infinity or nan
2818                     else if (is_match(chars[1], U'i', U'n', U'I', U'N'))
2819                         val.reset(new value{ parse_inf_or_nan() });
2820                 }
2821 
2822                 return_if_error({});
2823                 if (val)
2824                     break;
2825 
2826                 // match trait masks against what they can match exclusively.
2827                 // all correct value parses will come out of this list, so doing this as a switch is likely to
2828                 // be a better friend to the optimizer on the success path (failure path can be slow but that
2829                 // doesn't matter much).
2830                 switch (unwrap_enum(traits))
2831                 {
2832                     // binary integers
2833                     // 0b10
2834                     case bzero_msk | has_b:
2835                         val.reset(new value{ parse_integer<2>() });
2836                         val->ref_cast<int64_t>().flags(value_flags::format_as_binary);
2837                         break;
2838 
2839                     // octal integers
2840                     // 0o10
2841                     case bzero_msk | has_o:
2842                         val.reset(new value{ parse_integer<8>() });
2843                         val->ref_cast<int64_t>().flags(value_flags::format_as_octal);
2844                         break;
2845 
2846                     // decimal integers
2847                     // 00
2848                     // 10
2849                     // +10
2850                     // -10
2851                     case bzero_msk: [[fallthrough]];
2852                     case bdigit_msk: [[fallthrough]];
2853                     case begins_sign | has_digits | has_minus: [[fallthrough]];
2854                     case begins_sign | has_digits | has_plus:
2855                     {
2856                         // if the value was so long we exhausted the history buffer it's reasonable to assume
2857                         // there was more and the value's actual type is impossible to identify without making the
2858                         // buffer bigger (since it could have actually been a float), so emit an error.
2859                         //
2860                         // (this will likely only come up during fuzzing and similar scenarios)
2861                         static constexpr size_t max_numeric_value_length =
2862                             utf8_buffered_reader::max_history_length - 2u;
2863                         if TOML_UNLIKELY(!eof_while_scanning && advance_count > max_numeric_value_length)
2864                             set_error_and_return_default("numeric value too long to identify type - cannot exceed "sv,
2865                                                          max_numeric_value_length,
2866                                                          " characters"sv);
2867 
2868                         val.reset(new value{ parse_integer<10>() });
2869                         break;
2870                     }
2871 
2872                     // hexadecimal integers
2873                     // 0x10
2874                     case bzero_msk | has_x:
2875                         val.reset(new value{ parse_integer<16>() });
2876                         val->ref_cast<int64_t>().flags(value_flags::format_as_hexadecimal);
2877                         break;
2878 
2879                     // decimal floats
2880                     // 0e1
2881                     // 0e-1
2882                     // 0e+1
2883                     // 0.0
2884                     // 0.0e1
2885                     // 0.0e-1
2886                     // 0.0e+1
2887                     case bzero_msk | has_e: [[fallthrough]];
2888                     case bzero_msk | has_e | has_minus: [[fallthrough]];
2889                     case bzero_msk | has_e | has_plus: [[fallthrough]];
2890                     case bzero_msk | has_dot: [[fallthrough]];
2891                     case bzero_msk | has_dot | has_e: [[fallthrough]];
2892                     case bzero_msk | has_dot | has_e | has_minus: [[fallthrough]];
2893                     case bzero_msk | has_dot | has_e | has_plus: [[fallthrough]];
2894                     // 1e1
2895                     // 1e-1
2896                     // 1e+1
2897                     // 1.0
2898                     // 1.0e1
2899                     // 1.0e-1
2900                     // 1.0e+1
2901                     case bdigit_msk | has_e: [[fallthrough]];
2902                     case bdigit_msk | has_e | has_minus: [[fallthrough]];
2903                     case bdigit_msk | has_e | has_plus: [[fallthrough]];
2904                     case bdigit_msk | has_dot: [[fallthrough]];
2905                     case bdigit_msk | has_dot | has_e: [[fallthrough]];
2906                     case bdigit_msk | has_dot | has_e | has_minus: [[fallthrough]];
2907                     case bdigit_msk | has_dot | has_e | has_plus: [[fallthrough]];
2908                     // +1e1
2909                     // +1.0
2910                     // +1.0e1
2911                     // +1.0e+1
2912                     // +1.0e-1
2913                     // -1.0e+1
2914                     case begins_sign | has_digits | has_e | has_plus: [[fallthrough]];
2915                     case begins_sign | has_digits | has_dot | has_plus: [[fallthrough]];
2916                     case begins_sign | has_digits | has_dot | has_e | has_plus: [[fallthrough]];
2917                     case begins_sign | has_digits | has_dot | has_e | signs_msk: [[fallthrough]];
2918                     // -1e1
2919                     // -1e+1
2920                     // +1e-1
2921                     // -1.0
2922                     // -1.0e1
2923                     // -1.0e-1
2924                     case begins_sign | has_digits | has_e | has_minus: [[fallthrough]];
2925                     case begins_sign | has_digits | has_e | signs_msk: [[fallthrough]];
2926                     case begins_sign | has_digits | has_dot | has_minus: [[fallthrough]];
2927                     case begins_sign | has_digits | has_dot | has_e | has_minus:
2928                         val.reset(new value{ parse_float() });
2929                         break;
2930 
2931                     // hexadecimal floats
2932                     // 0x10p0
2933                     // 0x10p-0
2934                     // 0x10p+0
2935                     case bzero_msk | has_x | has_p: [[fallthrough]];
2936                     case bzero_msk | has_x | has_p | has_minus: [[fallthrough]];
2937                     case bzero_msk | has_x | has_p | has_plus: [[fallthrough]];
2938                     // -0x10p0
2939                     // -0x10p-0
2940                     // +0x10p0
2941                     // +0x10p+0
2942                     // -0x10p+0
2943                     // +0x10p-0
2944                     case begins_sign | has_digits | has_x | has_p | has_minus: [[fallthrough]];
2945                     case begins_sign | has_digits | has_x | has_p | has_plus: [[fallthrough]];
2946                     case begins_sign | has_digits | has_x | has_p | signs_msk: [[fallthrough]];
2947                     // 0x10.1p0
2948                     // 0x10.1p-0
2949                     // 0x10.1p+0
2950                     case bzero_msk | has_x | has_dot | has_p: [[fallthrough]];
2951                     case bzero_msk | has_x | has_dot | has_p | has_minus: [[fallthrough]];
2952                     case bzero_msk | has_x | has_dot | has_p | has_plus: [[fallthrough]];
2953                     // -0x10.1p0
2954                     // -0x10.1p-0
2955                     // +0x10.1p0
2956                     // +0x10.1p+0
2957                     // -0x10.1p+0
2958                     // +0x10.1p-0
2959                     case begins_sign | has_digits | has_x | has_dot | has_p | has_minus: [[fallthrough]];
2960                     case begins_sign | has_digits | has_x | has_dot | has_p | has_plus: [[fallthrough]];
2961                     case begins_sign | has_digits | has_x | has_dot | has_p | signs_msk:
2962                         val.reset(new value{ parse_hex_float() });
2963                         break;
2964 
2965                     // times
2966                     // HH:MM
2967                     // HH:MM:SS
2968                     // HH:MM:SS.FFFFFF
2969                     case bzero_msk | has_colon: [[fallthrough]];
2970                     case bzero_msk | has_colon | has_dot: [[fallthrough]];
2971                     case bdigit_msk | has_colon: [[fallthrough]];
2972                     case bdigit_msk | has_colon | has_dot: val.reset(new value{ parse_time() }); break;
2973 
2974                     // local dates
2975                     // YYYY-MM-DD
2976                     case bzero_msk | has_minus: [[fallthrough]];
2977                     case bdigit_msk | has_minus: val.reset(new value{ parse_date() }); break;
2978 
2979                     // date-times
2980                     // YYYY-MM-DDTHH:MM
2981                     // YYYY-MM-DDTHH:MM-HH:MM
2982                     // YYYY-MM-DDTHH:MM+HH:MM
2983                     // YYYY-MM-DD HH:MM
2984                     // YYYY-MM-DD HH:MM-HH:MM
2985                     // YYYY-MM-DD HH:MM+HH:MM
2986                     // YYYY-MM-DDTHH:MM:SS
2987                     // YYYY-MM-DDTHH:MM:SS-HH:MM
2988                     // YYYY-MM-DDTHH:MM:SS+HH:MM
2989                     // YYYY-MM-DD HH:MM:SS
2990                     // YYYY-MM-DD HH:MM:SS-HH:MM
2991                     // YYYY-MM-DD HH:MM:SS+HH:MM
2992                     case bzero_msk | has_minus | has_colon | has_t: [[fallthrough]];
2993                     case bzero_msk | signs_msk | has_colon | has_t: [[fallthrough]];
2994                     case bdigit_msk | has_minus | has_colon | has_t: [[fallthrough]];
2995                     case bdigit_msk | signs_msk | has_colon | has_t: [[fallthrough]];
2996                     // YYYY-MM-DDTHH:MM:SS.FFFFFF
2997                     // YYYY-MM-DDTHH:MM:SS.FFFFFF-HH:MM
2998                     // YYYY-MM-DDTHH:MM:SS.FFFFFF+HH:MM
2999                     // YYYY-MM-DD HH:MM:SS.FFFFFF
3000                     // YYYY-MM-DD HH:MM:SS.FFFFFF-HH:MM
3001                     // YYYY-MM-DD HH:MM:SS.FFFFFF+HH:MM
3002                     case bzero_msk | has_minus | has_colon | has_dot | has_t: [[fallthrough]];
3003                     case bzero_msk | signs_msk | has_colon | has_dot | has_t: [[fallthrough]];
3004                     case bdigit_msk | has_minus | has_colon | has_dot | has_t: [[fallthrough]];
3005                     case bdigit_msk | signs_msk | has_colon | has_dot | has_t: [[fallthrough]];
3006                     // YYYY-MM-DDTHH:MMZ
3007                     // YYYY-MM-DD HH:MMZ
3008                     // YYYY-MM-DDTHH:MM:SSZ
3009                     // YYYY-MM-DD HH:MM:SSZ
3010                     // YYYY-MM-DDTHH:MM:SS.FFFFFFZ
3011                     // YYYY-MM-DD HH:MM:SS.FFFFFFZ
3012                     case bzero_msk | has_minus | has_colon | has_z | has_t: [[fallthrough]];
3013                     case bzero_msk | has_minus | has_colon | has_dot | has_z | has_t: [[fallthrough]];
3014                     case bdigit_msk | has_minus | has_colon | has_z | has_t: [[fallthrough]];
3015                     case bdigit_msk | has_minus | has_colon | has_dot | has_z | has_t:
3016                         val.reset(new value{ parse_date_time() });
3017                         break;
3018                 }
3019             }
3020             while (false);
3021 
3022             if (!val)
3023             {
3024                 set_error_at(begin_pos, "could not determine value type"sv);
3025                 return_after_error({});
3026             }
3027 
3028             val->source_ = { begin_pos, current_position(1), reader.source_path() };
3029             return val;
3030         }
3031 
3032         TOML_NEVER_INLINE
3033         bool parse_key()
3034         {
3035             return_if_error({});
3036             assert_not_eof();
3037             TOML_ASSERT_ASSUME(is_bare_key_character(*cp) || is_string_delimiter(*cp));
3038             push_parse_scope("key"sv);
3039 
3040             key_buffer.clear();
3041             recording_whitespace = false;
3042 
3043             while (!is_error())
3044             {
3045                 std::string_view key_segment;
3046                 const auto key_begin = current_position();
3047 
3048                 // bare_key_segment
3049                 if (is_bare_key_character(*cp))
3050                     key_segment = parse_bare_key_segment();
3051 
3052                 // "quoted key segment"
3053                 else if (is_string_delimiter(*cp))
3054                 {
3055                     const auto begin_pos = cp->position;
3056 
3057                     recording_whitespace = true;
3058                     parsed_string str    = parse_string();
3059                     recording_whitespace = false;
3060                     return_if_error({});
3061 
3062                     if (str.was_multi_line)
3063                     {
3064                         set_error_at(begin_pos,
3065                                      "multi-line strings are prohibited in "sv,
3066                                      key_buffer.empty() ? ""sv : "dotted "sv,
3067                                      "keys"sv);
3068                         return_after_error({});
3069                     }
3070                     else
3071                         key_segment = str.value;
3072                 }
3073 
3074                 // ???
3075                 else
3076                     set_error_and_return_default("expected bare key starting character or string delimiter, saw '"sv,
3077                                                  to_sv(*cp),
3078                                                  "'"sv);
3079 
3080                 const auto key_end = current_position();
3081 
3082                 // whitespace following the key segment
3083                 consume_leading_whitespace();
3084 
3085                 // store segment
3086                 key_buffer.push_back(key_segment, key_begin, key_end);
3087 
3088                 // eof or no more key to come
3089                 if (is_eof() || *cp != U'.')
3090                     break;
3091 
3092                 // was a dotted key - go around again
3093                 advance_and_return_if_error_or_eof({});
3094                 consume_leading_whitespace();
3095                 set_error_and_return_if_eof({});
3096             }
3097             return_if_error({});
3098 
3099             return true;
3100         }
3101 
3102         TOML_NODISCARD
3103         key make_key(size_t segment_index) const
3104         {
3105             TOML_ASSERT(key_buffer.size() > segment_index);
3106 
3107             return key{
3108                 key_buffer[segment_index],
3109                 source_region{ key_buffer.starts[segment_index], key_buffer.ends[segment_index], root.source().path }
3110             };
3111         }
3112 
3113         TOML_NODISCARD
3114         TOML_NEVER_INLINE
3115         table* parse_table_header()
3116         {
3117             return_if_error({});
3118             assert_not_eof();
3119             TOML_ASSERT_ASSUME(*cp == U'[');
3120             push_parse_scope("table header"sv);
3121 
3122             const source_position header_begin_pos = cp->position;
3123             source_position header_end_pos;
3124             bool is_arr = false;
3125 
3126             // parse header
3127             {
3128                 // skip first '['
3129                 advance_and_return_if_error_or_eof({});
3130 
3131                 // skip past any whitespace that followed the '['
3132                 const bool had_leading_whitespace = consume_leading_whitespace();
3133                 set_error_and_return_if_eof({});
3134 
3135                 // skip second '[' (if present)
3136                 if (*cp == U'[')
3137                 {
3138                     if (had_leading_whitespace)
3139                         set_error_and_return_default(
3140                             "[[array-of-table]] brackets must be contiguous (i.e. [ [ this ] ] is prohibited)"sv);
3141 
3142                     is_arr = true;
3143                     advance_and_return_if_error_or_eof({});
3144 
3145                     // skip past any whitespace that followed the '['
3146                     consume_leading_whitespace();
3147                     set_error_and_return_if_eof({});
3148                 }
3149 
3150                 // check for a premature closing ']'
3151                 if (*cp == U']')
3152                     set_error_and_return_default("tables with blank bare keys are explicitly prohibited"sv);
3153 
3154                 // get the actual key
3155                 start_recording();
3156                 parse_key();
3157                 stop_recording(1u);
3158                 return_if_error({});
3159 
3160                 // skip past any whitespace that followed the key
3161                 consume_leading_whitespace();
3162                 return_if_error({});
3163                 set_error_and_return_if_eof({});
3164 
3165                 // consume the closing ']'
3166                 if (*cp != U']')
3167                     set_error_and_return_default("expected ']', saw '"sv, to_sv(*cp), "'"sv);
3168                 if (is_arr)
3169                 {
3170                     advance_and_return_if_error_or_eof({});
3171                     if (*cp != U']')
3172                         set_error_and_return_default("expected ']', saw '"sv, to_sv(*cp), "'"sv);
3173                 }
3174                 advance_and_return_if_error({});
3175                 header_end_pos = current_position(1);
3176 
3177                 // handle the rest of the line after the header
3178                 consume_leading_whitespace();
3179                 if (!is_eof() && !consume_comment() && !consume_line_break())
3180                     set_error_and_return_default("expected a comment or whitespace, saw '"sv, to_sv(cp), "'"sv);
3181             }
3182             TOML_ASSERT(!key_buffer.empty());
3183 
3184             // check if each parent is a table/table array, or can be created implicitly as a table.
3185             table* parent = &root;
3186             for (size_t i = 0, e = key_buffer.size() - 1u; i < e; i++)
3187             {
3188                 const std::string_view segment = key_buffer[i];
3189                 auto pit                       = parent->lower_bound(segment);
3190 
3191                 // parent already existed
3192                 if (pit != parent->end() && pit->first == segment)
3193                 {
3194                     node& p = pit->second;
3195 
3196                     if (auto tbl = p.as_table())
3197                     {
3198                         // adding to closed inline tables is illegal
3199                         if (tbl->is_inline() && !impl::find(open_inline_tables.begin(), open_inline_tables.end(), tbl))
3200                             set_error_and_return_default("cannot insert '"sv,
3201                                                          to_sv(recording_buffer),
3202                                                          "' into existing inline table"sv);
3203 
3204                         parent = tbl;
3205                     }
3206                     else if (auto arr = p.as_array(); arr && impl::find(table_arrays.begin(), table_arrays.end(), arr))
3207                     {
3208                         // table arrays are a special case;
3209                         // the spec dictates we select the most recently declared element in the array.
3210                         TOML_ASSERT(!arr->empty());
3211                         TOML_ASSERT(arr->back().is_table());
3212                         parent = &arr->back().ref_cast<table>();
3213                     }
3214                     else
3215                     {
3216                         if (!is_arr && p.type() == node_type::table)
3217                             set_error_and_return_default("cannot redefine existing table '"sv,
3218                                                          to_sv(recording_buffer),
3219                                                          "'"sv);
3220                         else
3221                             set_error_and_return_default("cannot redefine existing "sv,
3222                                                          to_sv(p.type()),
3223                                                          " '"sv,
3224                                                          to_sv(recording_buffer),
3225                                                          "' as "sv,
3226                                                          is_arr ? "array-of-tables"sv : "table"sv);
3227                     }
3228                 }
3229 
3230                 // need to create a new implicit table
3231                 else
3232                 {
3233                     pit       = parent->emplace_hint<table>(pit, make_key(i));
3234                     table& p  = pit->second.ref_cast<table>();
3235                     p.source_ = { header_begin_pos, header_end_pos, reader.source_path() };
3236 
3237                     implicit_tables.push_back(&p);
3238                     parent = &p;
3239                 }
3240             }
3241 
3242             const auto last_segment = key_buffer.back();
3243             auto it                 = parent->lower_bound(last_segment);
3244 
3245             // if there was already a matching node some sanity checking is necessary;
3246             // this is ok if we're making an array and the existing element is already an array (new element)
3247             // or if we're making a table and the existing element is an implicitly-created table (promote it),
3248             // otherwise this is a redefinition error.
3249             if (it != parent->end() && it->first == last_segment)
3250             {
3251                 node& matching_node = it->second;
3252                 if (auto arr = matching_node.as_array();
3253                     is_arr && arr && impl::find(table_arrays.begin(), table_arrays.end(), arr))
3254                 {
3255                     table& tbl  = arr->emplace_back<table>();
3256                     tbl.source_ = { header_begin_pos, header_end_pos, reader.source_path() };
3257                     return &tbl;
3258                 }
3259 
3260                 else if (auto tbl = matching_node.as_table(); !is_arr && tbl && !implicit_tables.empty())
3261                 {
3262                     if (auto found = impl::find(implicit_tables.begin(), implicit_tables.end(), tbl); found)
3263                     {
3264                         bool ok = true;
3265                         if (!tbl->empty())
3266                         {
3267                             for (auto& [_, child] : *tbl)
3268                             {
3269                                 if (!child.is_table() && !child.is_array_of_tables())
3270                                 {
3271                                     ok = false;
3272                                     break;
3273                                 }
3274                             }
3275                         }
3276 
3277                         if (ok)
3278                         {
3279                             implicit_tables.erase(implicit_tables.cbegin() + (found - implicit_tables.data()));
3280                             tbl->source_.begin = header_begin_pos;
3281                             tbl->source_.end   = header_end_pos;
3282                             return tbl;
3283                         }
3284                     }
3285                 }
3286 
3287                 // if we get here it's a redefinition error.
3288                 if (!is_arr && matching_node.type() == node_type::table)
3289                 {
3290                     set_error_at(header_begin_pos,
3291                                  "cannot redefine existing table '"sv,
3292                                  to_sv(recording_buffer),
3293                                  "'"sv);
3294                     return_after_error({});
3295                 }
3296                 else
3297                 {
3298                     set_error_at(header_begin_pos,
3299                                  "cannot redefine existing "sv,
3300                                  to_sv(matching_node.type()),
3301                                  " '"sv,
3302                                  to_sv(recording_buffer),
3303                                  "' as "sv,
3304                                  is_arr ? "array-of-tables"sv : "table"sv);
3305                     return_after_error({});
3306                 }
3307             }
3308 
3309             // there was no matching node, sweet - we can freely instantiate a new table/table array.
3310             else
3311             {
3312                 auto last_key = make_key(key_buffer.size() - 1u);
3313 
3314                 // if it's an array we need to make the array and it's first table element,
3315                 // set the starting regions, and return the table element
3316                 if (is_arr)
3317                 {
3318                     it             = parent->emplace_hint<array>(it, std::move(last_key));
3319                     array& tbl_arr = it->second.ref_cast<array>();
3320                     table_arrays.push_back(&tbl_arr);
3321                     tbl_arr.source_ = { header_begin_pos, header_end_pos, reader.source_path() };
3322 
3323                     table& tbl  = tbl_arr.emplace_back<table>();
3324                     tbl.source_ = { header_begin_pos, header_end_pos, reader.source_path() };
3325                     return &tbl;
3326                 }
3327 
3328                 // otherwise we're just making a table
3329                 else
3330                 {
3331                     it          = parent->emplace_hint<table>(it, std::move(last_key));
3332                     table& tbl  = it->second.ref_cast<table>();
3333                     tbl.source_ = { header_begin_pos, header_end_pos, reader.source_path() };
3334                     return &tbl;
3335                 }
3336             }
3337         }
3338 
3339         TOML_NEVER_INLINE
3340         bool parse_key_value_pair_and_insert(table* tbl)
3341         {
3342             return_if_error({});
3343             assert_not_eof();
3344             TOML_ASSERT_ASSUME(is_string_delimiter(*cp) || is_bare_key_character(*cp));
3345             push_parse_scope("key-value pair"sv);
3346 
3347             // read the key into the key buffer
3348             start_recording();
3349             parse_key();
3350             stop_recording(1u);
3351             return_if_error({});
3352             TOML_ASSERT(key_buffer.size() >= 1u);
3353 
3354             // skip past any whitespace that followed the key
3355             consume_leading_whitespace();
3356             set_error_and_return_if_eof({});
3357 
3358             // '='
3359             if (*cp != U'=')
3360                 set_error_and_return_default("expected '=', saw '"sv, to_sv(*cp), "'"sv);
3361             advance_and_return_if_error_or_eof({});
3362 
3363             // skip past any whitespace that followed the '='
3364             consume_leading_whitespace();
3365             return_if_error({});
3366             set_error_and_return_if_eof({});
3367 
3368             // check that the next character could actually be a value
3369             if (is_value_terminator(*cp))
3370                 set_error_and_return_default("expected value, saw '"sv, to_sv(*cp), "'"sv);
3371 
3372             // if it's a dotted kvp we need to spawn the parent sub-tables if necessary,
3373             // and set the target table to the second-to-last one in the chain
3374             if (key_buffer.size() > 1u)
3375             {
3376                 for (size_t i = 0; i < key_buffer.size() - 1u; i++)
3377                 {
3378                     const std::string_view segment = key_buffer[i];
3379                     auto pit                       = tbl->lower_bound(segment);
3380 
3381                     // parent already existed
3382                     if (pit != tbl->end() && pit->first == segment)
3383                     {
3384                         table* p = pit->second.as_table();
3385 
3386                         // redefinition
3387                         if TOML_UNLIKELY(!p
3388                             || !(impl::find(dotted_key_tables.begin(), dotted_key_tables.end(), p)
3389                                  || impl::find(implicit_tables.begin(), implicit_tables.end(), p)))
3390                         {
3391                             set_error_at(key_buffer.starts[i],
3392                                          "cannot redefine existing "sv,
3393                                          to_sv(pit->second.type()),
3394                                          " as dotted key-value pair"sv);
3395                             return_after_error({});
3396                         }
3397 
3398                         tbl = p;
3399                     }
3400 
3401                     // need to create a new implicit table
3402                     else
3403                     {
3404                         pit       = tbl->emplace_hint<table>(pit, make_key(i));
3405                         table& p  = pit->second.ref_cast<table>();
3406                         p.source_ = pit->first.source();
3407 
3408                         dotted_key_tables.push_back(&p);
3409                         tbl = &p;
3410                     }
3411                 }
3412             }
3413 
3414             // ensure this isn't a redefinition
3415             const std::string_view last_segment = key_buffer.back();
3416             auto it                             = tbl->lower_bound(last_segment);
3417             if (it != tbl->end() && it->first == last_segment)
3418             {
3419                 set_error("cannot redefine existing "sv,
3420                           to_sv(it->second.type()),
3421                           " '"sv,
3422                           to_sv(recording_buffer),
3423                           "'"sv);
3424                 return_after_error({});
3425             }
3426 
3427             // create the key first since the key buffer will likely get overwritten during value parsing (inline
3428             // tables)
3429             auto last_key = make_key(key_buffer.size() - 1u);
3430 
3431             // now we can actually parse the value
3432             node_ptr val = parse_value();
3433             return_if_error({});
3434 
3435             tbl->emplace_hint<node_ptr>(it, std::move(last_key), std::move(val));
3436             return true;
3437         }
3438 
3439         void parse_document()
3440         {
3441             assert_not_error();
3442             assert_not_eof();
3443             push_parse_scope("root table"sv);
3444 
3445             table* current_table = &root;
3446 
3447             do
3448             {
3449                 return_if_error();
3450 
3451                 // leading whitespace, line endings, comments
3452                 if (consume_leading_whitespace() || consume_line_break() || consume_comment())
3453                     continue;
3454                 return_if_error();
3455 
3456                 // [tables]
3457                 // [[table array]]
3458                 if (*cp == U'[')
3459                     current_table = parse_table_header();
3460 
3461                 // bare_keys
3462                 // dotted.keys
3463                 // "quoted keys"
3464                 else if (is_bare_key_character(*cp) || is_string_delimiter(*cp))
3465                 {
3466                     push_parse_scope("key-value pair"sv);
3467 
3468                     parse_key_value_pair_and_insert(current_table);
3469 
3470                     // handle the rest of the line after the kvp
3471                     // (this is not done in parse_key_value_pair() because that is also used for inline tables)
3472                     consume_leading_whitespace();
3473                     return_if_error();
3474                     if (!is_eof() && !consume_comment() && !consume_line_break())
3475                         set_error("expected a comment or whitespace, saw '"sv, to_sv(cp), "'"sv);
3476                 }
3477 
3478                 else // ??
3479                     set_error("expected keys, tables, whitespace or comments, saw '"sv, to_sv(cp), "'"sv);
3480             }
3481             while (!is_eof());
3482 
3483             auto eof_pos     = current_position(1);
3484             root.source_.end = eof_pos;
3485             if (current_table && current_table != &root && current_table->source_.end <= current_table->source_.begin)
3486                 current_table->source_.end = eof_pos;
3487         }
3488 
3489         static void update_region_ends(node& nde) noexcept
3490         {
3491             const auto type = nde.type();
3492             if (type > node_type::array)
3493                 return;
3494 
3495             if (type == node_type::table)
3496             {
3497                 auto& tbl = nde.ref_cast<table>();
3498                 if (tbl.is_inline()) // inline tables (and all their inline descendants) are already correctly
3499                                      // terminated
3500                     return;
3501 
3502                 auto end = nde.source_.end;
3503                 for (auto&& [k, v] : tbl)
3504                 {
3505                     TOML_UNUSED(k);
3506                     update_region_ends(v);
3507                     if (end < v.source_.end)
3508                         end = v.source_.end;
3509                 }
3510             }
3511             else // arrays
3512             {
3513                 auto& arr = nde.ref_cast<array>();
3514                 auto end  = nde.source_.end;
3515                 for (auto&& v : arr)
3516                 {
3517                     update_region_ends(v);
3518                     if (end < v.source_.end)
3519                         end = v.source_.end;
3520                 }
3521                 nde.source_.end = end;
3522             }
3523         }
3524 
3525       public:
3526         parser(utf8_reader_interface&& reader_) //
3527             : reader{ reader_ }
3528         {
3529             root.source_ = { prev_pos, prev_pos, reader.source_path() };
3530 
3531             if (!reader.peek_eof())
3532             {
3533                 cp = reader.read_next();
3534 
3535 #if !TOML_EXCEPTIONS
3536                 if (reader.error())
3537                 {
3538                     err = std::move(reader.error());
3539                     return;
3540                 }
3541 #endif
3542 
3543                 if (cp)
3544                     parse_document();
3545             }
3546 
3547             update_region_ends(root);
3548         }
3549 
3550         TOML_NODISCARD
3551         operator parse_result() && noexcept
3552         {
3553 #if TOML_EXCEPTIONS
3554 
3555             return { std::move(root) };
3556 
3557 #else
3558 
3559             if (err)
3560                 return parse_result{ *std::move(err) };
3561             else
3562                 return parse_result{ std::move(root) };
3563 
3564 #endif
3565         }
3566     };
3567 
3568     TOML_EXTERNAL_LINKAGE
3569     node_ptr parser::parse_array()
3570     {
3571         return_if_error({});
3572         assert_not_eof();
3573         TOML_ASSERT_ASSUME(*cp == U'[');
3574         push_parse_scope("array"sv);
3575 
3576         // skip opening '['
3577         advance_and_return_if_error_or_eof({});
3578 
3579         node_ptr arr_ptr{ new array{} };
3580         array& arr = arr_ptr->ref_cast<array>();
3581         enum class TOML_CLOSED_ENUM parse_type : int
3582         {
3583             none,
3584             comma,
3585             val
3586         };
3587         parse_type prev = parse_type::none;
3588 
3589         while (!is_error())
3590         {
3591             while (consume_leading_whitespace() || consume_line_break() || consume_comment())
3592                 continue;
3593             set_error_and_return_if_eof({});
3594 
3595             // commas - only legal after a value
3596             if (*cp == U',')
3597             {
3598                 if (prev == parse_type::val)
3599                 {
3600                     prev = parse_type::comma;
3601                     advance_and_return_if_error_or_eof({});
3602                     continue;
3603                 }
3604                 set_error_and_return_default("expected value or closing ']', saw comma"sv);
3605             }
3606 
3607             // closing ']'
3608             else if (*cp == U']')
3609             {
3610                 advance_and_return_if_error({});
3611                 break;
3612             }
3613 
3614             // must be a value
3615             else
3616             {
3617                 if (prev == parse_type::val)
3618                 {
3619                     set_error_and_return_default("expected comma or closing ']', saw '"sv, to_sv(*cp), "'"sv);
3620                     continue;
3621                 }
3622                 prev = parse_type::val;
3623 
3624                 auto val = parse_value();
3625                 return_if_error({});
3626 
3627                 if (!arr.capacity())
3628                     arr.reserve(4u);
3629                 arr.emplace_back<node_ptr>(std::move(val));
3630             }
3631         }
3632 
3633         return_if_error({});
3634         return arr_ptr;
3635     }
3636 
3637     TOML_EXTERNAL_LINKAGE
3638     node_ptr parser::parse_inline_table()
3639     {
3640         return_if_error({});
3641         assert_not_eof();
3642         TOML_ASSERT_ASSUME(*cp == U'{');
3643         push_parse_scope("inline table"sv);
3644 
3645         // skip opening '{'
3646         advance_and_return_if_error_or_eof({});
3647 
3648         node_ptr tbl_ptr{ new table{} };
3649         table& tbl = tbl_ptr->ref_cast<table>();
3650         tbl.is_inline(true);
3651         table_vector_scope table_scope{ open_inline_tables, tbl };
3652 
3653         enum class TOML_CLOSED_ENUM parse_type : int
3654         {
3655             none,
3656             comma,
3657             kvp
3658         };
3659         parse_type prev = parse_type::none;
3660         while (!is_error())
3661         {
3662             if constexpr (TOML_LANG_UNRELEASED) // toml/issues/516 (newlines/trailing commas in inline tables)
3663             {
3664                 while (consume_leading_whitespace() || consume_line_break() || consume_comment())
3665                     continue;
3666             }
3667             else
3668             {
3669                 while (consume_leading_whitespace())
3670                     continue;
3671             }
3672             return_if_error({});
3673             set_error_and_return_if_eof({});
3674 
3675             // commas - only legal after a key-value pair
3676             if (*cp == U',')
3677             {
3678                 if (prev == parse_type::kvp)
3679                 {
3680                     prev = parse_type::comma;
3681                     advance_and_return_if_error_or_eof({});
3682                 }
3683                 else
3684                     set_error_and_return_default("expected key-value pair or closing '}', saw comma"sv);
3685             }
3686 
3687             // closing '}'
3688             else if (*cp == U'}')
3689             {
3690                 if constexpr (!TOML_LANG_UNRELEASED) // toml/issues/516 (newlines/trailing commas in inline tables)
3691                 {
3692                     if (prev == parse_type::comma)
3693                     {
3694                         set_error_and_return_default("expected key-value pair, saw closing '}' (dangling comma)"sv);
3695                         continue;
3696                     }
3697                 }
3698                 advance_and_return_if_error({});
3699                 break;
3700             }
3701 
3702             // key-value pair
3703             else if (is_string_delimiter(*cp) || is_bare_key_character(*cp))
3704             {
3705                 if (prev == parse_type::kvp)
3706                     set_error_and_return_default("expected comma or closing '}', saw '"sv, to_sv(*cp), "'"sv);
3707                 else
3708                 {
3709                     prev = parse_type::kvp;
3710                     parse_key_value_pair_and_insert(&tbl);
3711                 }
3712             }
3713 
3715             else
3716                 set_error_and_return_default("expected key or closing '}', saw '"sv, to_sv(*cp), "'"sv);
3717         }
3718 
3719         return_if_error({});
3720         return tbl_ptr;
3721     }
