3763     TOML_NODISCARD
3764     TOML_INTERNAL_LINKAGE
3765     parse_result do_parse_file(std::string_view file_path)
3766     {
3767 #if TOML_EXCEPTIONS
3768 #define TOML_PARSE_FILE_ERROR(msg, path)                                                                               \
3769     throw parse_error{ msg, source_position{}, std::make_shared<const std::string>(std::move(path)) }
3770 #else
3771 #define TOML_PARSE_FILE_ERROR(msg, path)                                                                               \
3772     return parse_result                                                                                                \
3773     {                                                                                                                  \
3774         parse_error                                                                                                    \
3775         {                                                                                                              \
3776             msg, source_position{}, std::make_shared<const std::string>(std::move(path))                               \
3777         }                                                                                                              \
3778     }
3779 #endif
3780 
3781         std::string file_path_str(file_path);
3782 
3783         // open file with a custom-sized stack buffer
3784         std::ifstream file;
3785         TOML_OVERALIGNED char file_buffer[sizeof(void*) * 1024u];
3786         file.rdbuf()->pubsetbuf(file_buffer, sizeof(file_buffer));
3787 #if TOML_WINDOWS
3788         file.open(impl::widen(file_path_str).c_str(), std::ifstream::in | std::ifstream::binary | std::ifstream::ate);
3789 #else
3790         file.open(file_path_str, std::ifstream::in | std::ifstream::binary | std::ifstream::ate);
3791 #endif
3792         if (!file.is_open())
3793             TOML_PARSE_FILE_ERROR("File could not be opened for reading", file_path_str);
3794 
3795         // get size
3796         const auto file_size = file.tellg();
3797         if (file_size == -1)
3798             TOML_PARSE_FILE_ERROR("Could not determine file size", file_path_str);
3799         file.seekg(0, std::ifstream::beg);
3800 
3801         // read the whole file into memory first if the file isn't too large
3802         constexpr auto large_file_threshold = 1024 * 1024 * 2; // 2 MB
3803         if (file_size <= large_file_threshold)
3804         {
3805             std::vector<char> file_data;
3806             file_data.resize(static_cast<size_t>(file_size));
3807             file.read(file_data.data(), static_cast<std::streamsize>(file_size));
3808             return parse(std::string_view{ file_data.data(), file_data.size() }, std::move(file_path_str));
3809         }
3810 
3811         // otherwise parse it using the streams
3812         else
3813             return parse(file, std::move(file_path_str));
3814 
3815 #undef TOML_PARSE_FILE_ERROR
3816     }
3817 }
3818 TOML_ANON_NAMESPACE_END;
3819 
3820 TOML_NAMESPACE_START
3821 {
3822     TOML_ABI_NAMESPACE_BOOL(TOML_EXCEPTIONS, ex, noex);
3823 
