57 #define FORCE_FAIL(...) FAIL(__VA_ARGS__)
58 #else
59 #define FORCE_FAIL(...)                                                                                                \
60     do                                                                                                                 \
61     {                                                                                                                  \
62         FAIL(__VA_ARGS__);                                                                                             \
63         std::exit(-1);                                                                                                 \
64         TOML_UNREACHABLE;                                                                                              \
65     }                                                                                                                  \
66     while (false)
67 #endif
