69 #define CHECK_SYMMETRIC_RELOP(lhs, op, rhs, result)                                                                    \
70     CHECK(((lhs)op(rhs)) == (result));                                                                                 \
71     CHECK(((rhs)op(lhs)) == (result))
72 
73 #define CHECK_SYMMETRIC_EQUAL(lhs, rhs)                                                                                \
74     CHECK_SYMMETRIC_RELOP(lhs, ==, rhs, true);                                                                         \
75     CHECK_SYMMETRIC_RELOP(lhs, !=, rhs, false)
76 
77 #define CHECK_SYMMETRIC_INEQUAL(lhs, rhs)                                                                              \
78     CHECK_SYMMETRIC_RELOP(lhs, ==, rhs, false);                                                                        \
79     CHECK_SYMMETRIC_RELOP(lhs, !=, rhs, true)
