136 TOML_PURE_GETTER
137 constexpr std::string_view trim_file_path(std::string_view sv) noexcept
138 {
139     const auto src = std::min(sv.rfind("\\"sv), sv.rfind("/"sv));
140     if (src != std::string_view::npos)
141         sv = sv.substr(src + 1_sz);
142     return sv;
143 }
